TextInput.code:
@Entry
@Component
struct TextInputExample {
  @State PassWordSrc1: Resource = $r('app.media.onIcon') // 密码可见时的图标资源
  @State PassWordSrc2: Resource = $r('app.media.offIcon') // 密码不可见时的图标资源
  @State TextError: string = '' // 错误提示信息
  @State Text: string = '' // 用户输入的文本
  @State NameText: string = 'test' // 预设的用户名

  @Builder itemEnd() {
    Select([{ value: 'KB' },
      { value: 'MB' },
      { value: 'GB' },
      { value: 'TB', }])
      .height("48vp") // 单位选择器的高度
      .borderRadius(0) // 单位选择器的边框圆角
      .selected(2) // 默认选中第三个选项
      .align(Alignment.Center) // 对齐方式为居中对齐
      .value('MB') // 默认选中的值
      .font({ size: 20, weight: 500 }) // 字体大小和粗细
      .fontColor('#182431') // 字体颜色
      .selectedOptionFont({ size: 20, weight: 400 }) // 选中选项的字体大小和粗细
      .optionFont({ size: 20, weight: 400 }) // 选项的字体大小和粗细
      .backgroundColor(Color.Transparent) // 背景颜色为透明
      .responseRegion({ height: "40vp", width: "80%", x: '10%', y: '6vp' }) // 响应区域
      .onSelect((index: number) => {
        console.info('Select:' + index) // 选中选项时的回调函数
      })
  }

  build() {
    Column({ space: 20 }) {

      TextInput({ placeholder: 'user define password icon' })
        .type(InputType.Password) // 输入类型为密码
        .width(380) // 宽度
        .height(60) // 高度
        .passwordIcon({ onIconSrc: this.PassWordSrc1, offIconSrc: this.PassWordSrc2 }) // 密码图标

      TextInput({ placeholder: 'underline style' })
        .showUnderline(true) // 显示下划线样式
        .width(380) // 宽度
        .height(60) // 高度
        .showError('Error') // 错误提示信息
        .showUnit(this.itemEnd) // 右侧的单位选择器

      Text(`用户名：${this.Text}`) // 显示用户名
        .width('95%') // 宽度
      TextInput({ placeholder: '请输入用户名', text: this.Text })
        .showUnderline(true) // 显示下划线样式
        .width(380) // 宽度
        .showError(this.TextError) // 错误提示信息
        .onChange((value: string) => {
          this.Text = value // 监听输入内容
        })
        .onSubmit((enterKey: EnterKeyType, event: SubmitEvent) => {
          if (this.Text == this.NameText) {
            this.TextError = '' // 输入内容正确时清除错误提示
          } else {
            this.TextError = '用户名输入错误' // 输入内容错误时显示错误提示
            this.Text = '' // 清空输入内容
            event.keepEditableState() // 保持编辑状态
          }
        })

    }.width('100%') // 布局宽度占据整个屏幕
  }
}

@Entry
@Component
struct TextInputExample {
  build() {
    Row() {
      Column() {
        // 标题文本，显示 "heightAdaptivePolicy"，字体大小为 9，颜色为 0xCCCCCC
        Text('heightAdaptivePolicy').fontSize(9).fontColor(0xCCCCCC)

        // 第一个文本输入框，显示 "This is the text without the height adaptive policy set"，宽度为 80%，高度为 50vp，边框宽度为 1vp，边距为 1vp
        TextInput({text: 'This is the text without the height adaptive policy set'})
          .width('80%').height(50).borderWidth(1).margin(1)

        // 第二个文本输入框，显示 "This is the text with the height adaptive policy set"，宽度为 80%，高度为 50vp，边框宽度为 1vp，边距为 1vp，最小字体大小为 4，最大字体大小为 40，最大行数为 3，高度自适应策略为 MAX_LINES_FIRST
        TextInput({text: 'This is the text with the height adaptive policy set'})
          .width('80%').height(50).borderWidth(1).margin(1)
          .minFontSize(4)
          .maxFontSize(40)
          .maxLines(3)
          .heightAdaptivePolicy(TextHeightAdaptivePolicy.MAX_LINES_FIRST)

        // 第三个文本输入框，显示 "This is the text with the height adaptive policy set"，宽度为 80%，高度为 50vp，边框宽度为 1vp，边距为 1vp，最小字体大小为 4，最大字体大小为 40，最大行数为 3，高度自适应策略为 MIN_FONT_SIZE_FIRST
        TextInput({text: 'This is the text with the height adaptive policy set'})
          .width('80%').height(50).borderWidth(1).margin(1)
          .minFontSize(4)
          .maxFontSize(40)
          .maxLines(3)
          .heightAdaptivePolicy(TextHeightAdaptivePolicy.MIN_FONT_SIZE_FIRST)

        // 第四个文本输入框，显示 "This is the text with the height adaptive policy set"，宽度为 80%，高度为 50vp，边框宽度为 1vp，边距为 1vp，最小字体大小为 4，最大字体大小为 40，最大行数为 3，高度自适应策略为 LAYOUT_CONSTRAINT_FIRST
        TextInput({text: 'This is the text with the height adaptive policy set'})
          .width('80%').height(50).borderWidth(1).margin(1)
          .minFontSize(4)
          .maxFontSize(40)
          .maxLines(3)
          .heightAdaptivePolicy(TextHeightAdaptivePolicy.LAYOUT_CONSTRAINT_FIRST)
      }.height('90%')
    }
    .width('90%')
    .margin(10)
  }
}

