// 导入ToolBar和ToolBarOptions组件
import { ToolBar, ToolBarOptions } from '@kit.ArkUI'

// 定义按钮状态的枚举
enum ItemState {
  ENABLE = 1, // 启用状态
  DISABLE = 2, // 禁用状态
  ACTIVATE = 3 // 激活状态
}

// 主入口组件
