LoadingProgress.code:
@Entry
@Component
struct LoadingProgressExample {
  build() {
    Column({ space: 5 }) { // 创建一个垂直布局的Column组件，子元素之间的间距为5个单位
      Text('Orbital LoadingProgress ') // 创建一个文本组件，内容为'Orbital LoadingProgress '
        .fontSize(9) // 设置文本的字体大小为9
        .fontColor(0xCCCCCC) // 设置文本的颜色为浅灰色（0xCCCCCC）
        .width('90%') // 设置文本的宽度为父容器的90%
      LoadingProgress() // 创建一个加载进度条组件
        .color(Color.Blue) // 设置加载进度条的颜色为蓝色
        .layoutWeight(1) // 设置加载进度条的布局权重为1，使其占据剩余的垂直空间
    }
    .width('100%') // 设置Column组件的宽度为100%
    .margin({ top: 5 }) // 设置Column组件的顶部边距为5个单位
  }
}

RelativeContainer.code:
@Entry
@Component
struct Index {
  build() {
    Row() {
      RelativeContainer() {
        // 创建一个宽度为100，高度为100的红色矩形
        Row().width(100).height(100)
          .backgroundColor("#FF3333")
          .alignRules({
            // 矩形的顶部与父容器的顶部对齐
            top: {anchor: "__container__", align: VerticalAlign.Top},
            // 矩形的底部与父容器的底部对齐
            bottom: {anchor: "__container__", align: VerticalAlign.Bottom},
            // 矩形的左侧与父容器的左侧对齐
            left: {anchor: "__container__", align: HorizontalAlign.Start},
            // 矩形的右侧与父容器的右侧对齐
            right: {anchor: "__container__", align: HorizontalAlign.End},
            // 设置矩形的垂直偏移，使其相对于父容器的顶部和底部偏移30%
            bias: {vertical: 0.3}
          })
          .id("row1")
      }
      .width(300).height(300)
      .margin({left: 50}) // 设置RelativeContainer的左边距为50
      .border({width:2, color: "#6699FF"}) // 为RelativeContainer添加一个宽度为2的蓝色边框
    }
    .height('100%') // 设置Row的高度为100%
  }
}

