translations.target_component_code:
RelativeContainer() {
  Text('20/04/1997')
    .width('75vp')
    .height('16vp')
    .margin({ left: '8vp', top: '8vp' })
    .fontColor($r('app.color.white'))
    .fontWeight(FontWeight.Bold)
    .alignRules({
      left: { anchor: '__container__', align: HorizontalAlign.Start },
      top: { anchor: '__container__', align: VerticalAlign.Top }
    })

  Text('20:18:35')
    .width('auto')
    .height('auto')
    .margin({ left: '8vp', top: '8vp' })
    .fontColor($r('app.color.white'))
    .alignRules({
      baseline: { anchor: 'order_date', align: VerticalAlign.Baseline },
      right: { anchor: '__container__', align: HorizontalAlign.End }
    })

  Divider()
    .width('100%')
    .height('1vp')
    .margin({ left: '8vp', top: '8vp' })
    .backgroundColor('#ec526576')
    .alignRules({
      top: { anchor: '__container__', align: VerticalAlign.Top }
    })

  Text('OrderId')
    .width('auto')
    .height('auto')
    .margin({ left: '8vp', top: '8vp' })
    .fontColor('#8795bb')
    .alignRules({
      top: { anchor: 'linearLayout', align: VerticalAlign.Bottom },
      left: { anchor: '__container__', align: HorizontalAlign.Start }
    })

  Text('Status')
    .width('auto')
    .height('17vp')
    .margin({ left: '8vp', top: '8vp' })
    .fontColor('#8795bb')
    .alignRules({
      top: { anchor: 'orderIdText', align: VerticalAlign.Bottom },
      left: { anchor: '__container__', align: HorizontalAlign.Start }
    })

  Text('Total Savings')
    .width('auto')
    .height('auto')
    .margin({ left: '8vp', top: '8vp' })
    .fontColor('#8795bb')
    .alignRules({
      top: { anchor: 'statustText', align: VerticalAlign.Bottom },
      left: { anchor: '__container__', align: HorizontalAlign.Start }
    })

  Text('Payable Amount')
    .width('auto')
    .height('auto')
    .margin({ left: '8vp', top: '8vp' })
    .fontColor('#8795bb')
    .alignRules({
      baseline: { anchor: 'payableAmount', align: VerticalAlign.Baseline },
      right: { anchor: 'payableAmount', align: HorizontalAlign.Start }
    })

  Divider()
    .width('100%')
    .height('1vp')
    .backgroundColor('#ec526576')
    .alignRules({
      bottom: { anchor: '__container__', align: VerticalAlign.Bottom },
      verticalBias: 0.82
    })

  Text('TextView')
    .width('auto')
    .height('auto')
    .fontColor('#FFFFFF')
    .alignRules({
      baseline: { anchor: 'orderIdText', align: VerticalAlign.Baseline },
      left: { anchor: 'status', align: HorizontalAlign.Start }
    })

  Text('TextView')
    .width('auto')
    .height('auto')
    .fontColor('#FFFFFF')
    .alignRules({
      top: { anchor: 'orderId', align: VerticalAlign.Bottom },
      left: { anchor: 'total_savings', align: HorizontalAlign.Start }
    })

  Text('TextView')
    .width('auto')
    .height('auto')
    .margin({ left: '8vp', top: '8vp' })
    .fontColor('#FFFFFF')
    .alignRules({
      baseline: { anchor: 'totalSavingsText', align: VerticalAlign.Baseline },
      right: { anchor: 'totalSavingsText', align: HorizontalAlign.End }
    })

  Text('TextView')
    .width('auto')
    .height('auto')
    .margin({ left: '8vp', top: '8vp' })
    .fontColor('#FFFFFF')
    .alignRules({
      bottom: { anchor: '__container__', align: VerticalAlign.Bottom },
      right: { anchor: '__container__', align: HorizontalAlign.End }
    })
}
.width('100%')
.height('100%')
.backgroundColor($r('app.media.b1'))

