Text.code:
@Entry
@Component
struct TextExample7 {
  // 创建一个TextController实例，用于控制文本的选择和菜单的显示
  controller: TextController = new TextController();
  // 创建一个TextOptions实例，用于配置文本的控制器
  options: TextOptions = { controller: this.controller };

  build() {
    Column() {
      Column() {
        // 创建一个Text组件，内容为undefined，使用options配置
        Text(undefined, this.options) {
          // 创建一个Span组件，内容为"Hello World"
          Span('Hello World')
          // 创建一个ImageSpan组件，内容为应用内的图标资源
          ImageSpan($r('app.media.icon'))
            .width('100px') // 设置图像宽度为100px
            .height('100px') // 设置图像高度为100px
            .objectFit(ImageFit.Fill) // 设置图像填充方式为完全填充
            .verticalAlign(ImageSpanAlignment.CENTER) // 设置图像垂直对齐方式为居中
        }
        .copyOption(CopyOptions.InApp) // 设置文本的复制选项为仅在应用内复制
        .bindSelectionMenu(TextSpanType.IMAGE, this.LongPressImageCustomMenu, TextResponseType.LONG_PRESS, {
          onDisappear: () => {
            console.info(`自定义选择菜单关闭时回调`); // 自定义选择菜单关闭时的回调函数
          },
          onAppear: () => {
            console.info(`自定义选择菜单弹出时回调`); // 自定义选择菜单弹出时的回调函数
          }
        })
        .bindSelectionMenu(TextSpanType.TEXT, this.RightClickTextCustomMenu, TextResponseType.RIGHT_CLICK) // 绑定文本的右键点击菜单
        .bindSelectionMenu(TextSpanType.MIXED, this.SelectMixCustomMenu, TextResponseType.SELECT) // 绑定混合内容的选中菜单
        .onTextSelectionChange((selectionStart: number, selectionEnd: number) => {
          console.info(`文本选中区域变化回调, selectionStart: ${selectionStart}, selectionEnd: ${selectionEnd}`); // 文本选中区域变化时的回调函数
        })
        .borderWidth(1) // 设置边框宽度为1vp
        .borderColor(Color.Red) // 设置边框颜色为红色
        .width(200) // 设置宽度为200vp
        .height(100) // 设置高度为100vp
      }
      .width('100%') // 设置宽度为100%
      .backgroundColor(Color.White) // 设置背景颜色为白色
      .alignItems(HorizontalAlign.Start) // 设置对齐方式为左对齐
      .padding(25) // 设置内边距为25vp
    }
    .height('100%') // 设置高度为100%
  }

  // 定义右键点击文本时的自定义菜单
  @Builder
  RightClickTextCustomMenu() {
    Column() {
      Menu() {
        MenuItemGroup() {
          MenuItem({ startIcon: $r('app.media.app_icon'), content: "Right Click Menu 1", labelInfo: "" })
            .onClick((event) => {
              this.controller.closeSelectionMenu(); // 点击菜单项时关闭菜单
            })
          MenuItem({ startIcon: $r('app.media.app_icon'), content: "Right Click Menu 2", labelInfo: "" })
          MenuItem({ startIcon: $r('app.media.app_icon'), content: "Right Click Menu 3", labelInfo: "" })
        }
      }
      .MenuStyles() // 应用菜单样式
    }
  }

  // 定义长按图像时的自定义菜单
  @Builder
  LongPressImageCustomMenu() {
    Column() {
      Menu() {
        MenuItemGroup() {
          MenuItem({ startIcon: $r('app.media.app_icon'), content: "Long Press Image Menu 1", labelInfo: "" })
            .onClick((event) => {
              this.controller.closeSelectionMenu(); // 点击菜单项时关闭菜单
            })
          MenuItem({ startIcon: $r('app.media.app_icon'), content: "Long Press Image Menu 2", labelInfo: "" })
          MenuItem({ startIcon: $r('app.media.app_icon'), content: "Long Press Image Menu 3", labelInfo: "" })
        }
      }
      .MenuStyles() // 应用菜单样式
    }
  }

  // 定义选择混合内容时的自定义菜单
  @Builder
  SelectMixCustomMenu() {
    Column() {
      Menu() {
        MenuItemGroup() {
          MenuItem({ startIcon: $r('app.media.app_icon'), content: "Select Mixed Menu 1", labelInfo: "" })
            .onClick((event) => {
              this.controller.closeSelectionMenu(); // 点击菜单项时关闭菜单
            })
          MenuItem({ startIcon: $r('app.media.app_icon'), content: "Select Mixed Menu 2", labelInfo: "" })
          MenuItem({ startIcon: $r('app.media.app_icon'), content: "Select Mixed Menu 3", labelInfo: "" })
        }
      }
      .MenuStyles() // 应用菜单样式
    }
  }
}

// 定义菜单的样式
@Extend(Menu)
function MenuStyles() {
  .radius($r('sys.float.ohos_id_corner_radius_card')) // 设置菜单的圆角半径
  .clip(true) // 设置菜单的裁剪方式为裁剪
  .backgroundColor('#F0F0F0') // 设置菜单的背景颜色为浅灰色
}

import { LengthMetrics } from '@kit.ArkUI'

// 自定义 Text 组件的行间距样式
@Extend(Text) function lineSpacingValue(LineSpacing: LengthMetrics|undefined) {
  .lineSpacing(LineSpacing) // 设置行间距
  .fontSize(12) // 设置字体大小为 12
  .border({ width: 1 }) // 设置文本边框宽度为 1
}

@Entry
@Component
struct TextExample8 {
  @State message1: string = "They can be classified as built-in components–those directly provided by the ArkUI framework and custom components – those defined by developers" +
    "The built-in components include buttons radio buttonsprogress indicators and text You can set the rendering effectof thesecomponents in method chaining mode," +
    "page components are divided into independent UI units to implementindependent creation development and reuse of different units on pages making pages more engineering-oriented.";
  @State lineBreakStrategyIndex: number = 0; // 当前换行策略索引
  @State lineBreakStrategy: LineBreakStrategy[] = [LineBreakStrategy.GREEDY, LineBreakStrategy.HIGH_QUALITY, LineBreakStrategy.BALANCED] // 换行策略数组
  @State lineBreakStrategyStr: string[] = ['GREEDY', 'HIGH_QUALITY', 'BALANCED'] // 换行策略字符串数组

  build() {
    Flex({ direction: FlexDirection.Column, alignItems: ItemAlign.Start, justifyContent: FlexAlign.SpaceBetween }) {
      Text('lineSpacing').fontSize(9).fontColor(0xCCCCCC) // 标题：行间距
      Text('This is a context with no lineSpacing set.')
        .lineSpacingValue(undefined) // 未设置行间距的文本
      Text( 'This is a context with lineSpacing set to 20_px.')
        .lineSpacingValue(LengthMetrics.px(20)) // 行间距设置为 20px 的文本
      Text('This is the context with lineSpacing set to 20_vp.')
        .lineSpacingValue(LengthMetrics.vp(20)) // 行间距设置为 20vp 的文本
      Text('This is the context with lineSpacing set to 20_fp.')
        .lineSpacingValue(LengthMetrics.fp(20)) // 行间距设置为 20fp 的文本
      Text('This is the context with lineSpacing set to 20_lpx.')
        .lineSpacingValue(LengthMetrics.lpx(20)) // 行间距设置为 20lpx 的文本
      Text('This is the context with lineSpacing set to 100%.')
        .lineSpacingValue(LengthMetrics.percent(1)) // 行间距设置为 100% 的文本
      Text('fontFeature').fontSize(9).fontColor(0xCCCCCC) // 标题：字体特性
      Text('This is ss01 on : 0123456789')
        .fontSize(20)
        .fontFeature("\"ss01\" on") // 启用 ss01 字体特性的文本
      Text('This is ss01 off: 0123456789')
        .fontSize(20)
        .fontFeature("\"ss01\" off") // 禁用 ss01 字体特性的文本
      Text('lineBreakStrategy').fontSize(9).fontColor(0xCCCCCC) // 标题：换行策略
      Text(this.message1)
        .fontSize(12)
        .border({ width: 1 })
        .padding(10)
        .width('100%')
        .lineBreakStrategy(this.lineBreakStrategy[this.lineBreakStrategyIndex]) // 根据当前换行策略索引设置换行策略的文本
      Row() {
        Button('当前lineBreakStrategy模式：' + this.lineBreakStrategyStr[this.lineBreakStrategyIndex]).onClick(() => {
          this.lineBreakStrategyIndex++ // 切换到下一个换行策略
          if(this.lineBreakStrategyIndex > (this.lineBreakStrategyStr.length - 1)) {
            this.lineBreakStrategyIndex = 0 // 如果超出数组范围，重置为第一个换行策略
          }
        })
      }
    }.height(600).width(350).padding({ left: 35, right: 35, top: 35 })
  }
}

