Progress.code:
@Entry
@Component
struct ProgressExample {
  // 定义一个线性渐变颜色，从黄色（偏移量0.5）到橙色（偏移量1.0）
  private gradientColor: LinearGradient = new LinearGradient([{ color: Color.Yellow, offset: 0.5 },
                                                              { color: Color.Orange, offset: 1.0 }])
  build() {
    Column({ space: 15 }) {
      // 创建一个文本组件，显示 "Loading Effect"，字体大小为 9vp，颜色为 0xCCCCCC，宽度为 90%
      Text('Loading Effect').fontSize(9).fontColor(0xCCCCCC).width('90%')

      // 创建一个环形进度条，初始值为 0，总进度为 100，类型为环形
      Progress({ value: 0, total: 100, type: ProgressType.Ring })
        .width(100) // 设置进度条宽度为 100vp
        .color(Color.Blue) // 设置进度条颜色为蓝色
        .style({ strokeWidth: 20, status: ProgressStatus.LOADING }) // 设置进度条线条宽度为 20vp，状态为加载中

      // 创建一个文本组件，显示 "Scan Effect"，字体大小为 9vp，颜色为 0xCCCCCC，宽度为 90%
      Text('Scan Effect').fontSize(9).fontColor(0xCCCCCC).width('90%')

      // 创建一个环形进度条，初始值为 30，总进度为 100，类型为环形
      Progress({ value: 30, total: 100, type: ProgressType.Ring })
        .width(100) // 设置进度条宽度为 100vp
        .color(Color.Orange) // 设置进度条颜色为橙色
        .style({ strokeWidth: 20, enableScanEffect: true }) // 设置进度条线条宽度为 20vp，启用扫描效果
    }.width('100%').padding({ top: 5 }) // 设置整个 Column 的宽度为 100%，顶部内边距为 5vp
  }
}

@Entry
@Component
struct ProgressExample {
  build() {
    Column({ space: 15 }) { // 创建一个垂直布局，子组件之间的间距为 15vp
      Row({ space: 40 }) { // 创建一个水平布局，子组件之间的间距为 40vp
        Progress({ value: 100, total: 100, type: ProgressType.Capsule }) // 创建一个胶囊形进度条，当前值为 100，总值为 100
          .width(100) // 设置进度条的宽度为 100vp
          .height(50) // 设置进度条的高度为 50vp
          .style({
            borderColor: Color.Blue, // 设置进度条的边框颜色为蓝色
            borderWidth: 1, // 设置进度条的边框宽度为 1vp
            content: 'Installing...', // 设置进度条的内容文本为 "Installing..."
            font: { size: 13, style: FontStyle.Normal }, // 设置进度条内容文本的字体大小为 13vp，字体样式为正常
            fontColor: Color.Gray, // 设置进度条内容文本的字体颜色为灰色
            enableScanEffect: false, // 禁用进度条的扫描效果
            showDefaultPercentage: false // 不显示默认的百分比
          })
      }
    }.width('100%') // 设置整个布局的宽度为 100%
     .padding({ top: 5 }) // 设置整个布局的上内边距为 5vp
  }
}

@Entry
@Component
struct ProgressExample {
  // 定义一个渐变色对象，用于设置进度条的颜色
  private gradientColor: LinearGradient = new LinearGradient([{ color: Color.Yellow, offset: 0.5 },
                                                              { color: Color.Orange, offset: 1.0 }])

  build() {
    Column({ space: 15 }) {
      // 标题文本，字体大小为 9，颜色为 0xCCCCCC，宽度为 90%
      Text('Gradient Color').fontSize(9).fontColor(0xCCCCCC).width('90%')

      // 环形进度条，当前值为 70，总进度为 100，类型为环形
      Progress({ value: 70, total: 100, type: ProgressType.Ring })
        .width(100) // 进度条宽度为 100vp
        .style({ strokeWidth: 20 }) // 进度条描边宽度为 20vp
        .color(this.gradientColor) // 进度条颜色为渐变色

      // 标题文本，字体大小为 9，颜色为 0xCCCCCC，宽度为 90%
      Text('Shadow').fontSize(9).fontColor(0xCCCCCC).width('90%')

      // 环形进度条，当前值为 70，总进度为 100，类型为环形
      Progress({ value: 70, total: 100, type: ProgressType.Ring })
        .width(120) // 进度条宽度为 120vp
        .color(Color.Orange) // 进度条颜色为橙色
        .style({ strokeWidth: 20, shadow: true }) // 进度条描边宽度为 20vp，启用阴影效果
    }.width('100%').padding({ top: 5 }) // 整个 Column 的宽度为 100%，顶部内边距为 5vp
  }
}

