@Entry
@Component
struct ImageExample1 {
  build() {
    Column() {
      Flex({ direction: FlexDirection.Column, alignItems: ItemAlign.Start }) {
        Row() {
          // 加载 PNG 图片，设置图片的宽度、高度和边距，并在图片底部添加文本标签 "png"
          Image($r('app.media.ic_camera_master_ai_leaf'))
            .width(110).height(110).margin(15)
            .overlay('png', { align: Alignment.Bottom, offset: { x: 0, y: 20 } })

          // 加载 GIF 图片，设置图片的宽度、高度和边距，并在图片底部添加文本标签 "gif"
          Image($r('app.media.loading'))
            .width(110).height(110).margin(15)
            .overlay('gif', { align: Alignment.Bottom, offset: { x: 0, y: 20 } })
        }
        Row() {
          // 加载 SVG 图片，设置图片的宽度、高度和边距，并在图片底部添加文本标签 "svg"
          Image($r('app.media.ic_camera_master_ai_clouded'))
            .width(110).height(110).margin(15)
            .overlay('svg', { align: Alignment.Bottom, offset: { x: 0, y: 20 } })

          // 加载 JPG 图片，设置图片的宽度、高度和边距，并在图片底部添加文本标签 "jpg"
          Image($r('app.media.ic_public_favor_filled'))
            .width(110).height(110).margin(15)
            .overlay('jpg', { align: Alignment.Bottom, offset: { x: 0, y: 20 } })
        }
      }
    }.height(320).width(360).padding({ right: 10, top: 10 })
  }
}

@Entry
@Component
struct ImageExample3 {
  // 定义三张图片资源
  private imageOne: Resource = $r('app.media.earth');
  private imageTwo: Resource = $r('app.media.star');
  private imageThree: Resource = $r('app.media.moveStar');

  // 定义两个状态变量，用于控制图片的显示
  @State src: Resource = this.imageOne;
  @State src2: Resource = this.imageThree;

  build() {
    Column() {
      // 第一张图片，点击后切换为第二张图片
      Image(this.src)
        .width(100) // 设置图片宽度为 100vp
        .height(100) // 设置图片高度为 100vp
        .onClick(() => {
          // 点击事件，切换图片为第二张图片
          this.src = this.imageTwo;
        })

      // 第二张图片，动画结束后切换为第一张图片
      Image(this.src2)
        .width(100) // 设置图片宽度为 100vp
        .height(100) // 设置图片高度为 100vp
        .onFinish(() => {
          // 动画结束事件，切换图片为第一张图片
          this.src2 = this.imageOne;
        })
    }
    .width('100%') // 设置布局宽度为 100%
    .height('100%') // 设置布局高度为 100%
  }
}

@Entry
@Component
struct Index {
  @State top: number = 40 // 定义图片顶部拉伸区域的初始值
  @State bottom: number = 5 // 定义图片底部拉伸区域的初始值
  @State left: number = 40 // 定义图片左侧拉伸区域的初始值
  @State right: number = 10 // 定义图片右侧拉伸区域的初始值

  build() {
    Column({ space: 5 }) { // 创建一个垂直布局，组件之间的间距为 5vp

      Image($r("app.media.sky")) // 加载名为 "sky" 的图片资源
        .width(200).height(200) // 设置图片的宽度和高度为 200vp
        .border({ width: 2, color: Color.Pink }) // 设置图片的边框宽度和颜色（粉色，2vp）
        .objectFit(ImageFit.Contain) // 设置图片的适应方式为包含（保持图片比例，尽可能填充区域）

      Image($r("app.media.sky")) // 加载名为 "sky" 的图片资源
        .resizable({ // 设置图片的可调整拉伸区域
          slice: {
            left: this.left, // 设置图片左侧拉伸区域的宽度
            right: this.right, // 设置图片右侧拉伸区域的宽度
            top: this.top, // 设置图片顶部拉伸区域的高度
            bottom: this.bottom // 设置图片底部拉伸区域的高度
          }
        })
        .width(200) // 设置图片的宽度为 200vp
        .height(200) // 设置图片的高度为 200vp
        .border({ width: 2, color: Color.Pink }) // 设置图片的边框宽度和颜色（粉色，2vp）
        .objectFit(ImageFit.Contain) // 设置图片的适应方式为包含（保持图片比例，尽可能填充区域）

      Row() { // 创建一个水平布局
        Button("add top to " + this.top).fontSize(10) // 创建一个按钮，点击后增加顶部拉伸区域的高度
          .onClick(() => {
            this.top += 2 // 每次点击增加 2vp
          })
        Button("add bottom to " + this.bottom).fontSize(10) // 创建一个按钮，点击后增加底部拉伸区域的高度
          .onClick(() => {
            this.bottom += 2 // 每次点击增加 2vp
          })
      }

      Row() { // 创建一个水平布局
        Button("add left to " + this.left).fontSize(10) // 创建一个按钮，点击后增加左侧拉伸区域的宽度
          .onClick(() => {
            this.left += 2 // 每次点击增加 2vp
          })
        Button("add right to " + this.right).fontSize(10) // 创建一个按钮，点击后增加右侧拉伸区域的宽度
          .onClick(() => {
            this.right += 2 // 每次点击增加 2vp
          })
      }

    }
    .justifyContent(FlexAlign.Start).width('100%').height('100%') // 设置布局的对齐方式为开始对齐，宽度为 100%，高度为 100%
  }
}

