@Entry
@Component
struct ImageExample3 {
  // 定义三张图片资源
  private imageOne: Resource = $r('app.media.earth');
  private imageTwo: Resource = $r('app.media.star');
  private imageThree: Resource = $r('app.media.moveStar');

  // 定义两个状态变量，用于控制图片的显示
  @State src: Resource = this.imageOne;
  @State src2: Resource = this.imageThree;

  build() {
    Column() {
      // 第一张图片，点击后切换为第二张图片
      Image(this.src)
        .width(100) // 设置图片宽度为 100vp
        .height(100) // 设置图片高度为 100vp
        .onClick(() => {
          // 点击事件，切换图片为第二张图片
          this.src = this.imageTwo;
        })

      // 第二张图片，动画结束后切换为第一张图片
      Image(this.src2)
        .width(100) // 设置图片宽度为 100vp
        .height(100) // 设置图片高度为 100vp
        .onFinish(() => {
          // 动画结束事件，切换图片为第一张图片
          this.src2 = this.imageOne;
        })
    }
    .width('100%') // 设置布局宽度为 100%
    .height('100%') // 设置布局高度为 100%
  }
}

import { http } from '@kit.NetworkKit'; // 导入网络请求工具包
import { BusinessError } from '@kit.BasicServicesKit'; // 导入业务错误处理工具包
import { image } from '@kit.ImageKit'; // 导入图片处理工具包

@Entry
@Component
struct ImageExample2 {
  @State pixelMapImg: PixelMap | undefined = undefined; // 定义一个状态变量，用于存储图片的PixelMap数据

  aboutToAppear() {
    this.requestImageUrl('https://www.example.com/xxx.png'); // 组件即将显示时，调用请求图片URL的方法
  }

  requestImageUrl(url: string) {
    http.createHttp().request(url, (error: BusinessError, data: http.HttpResponse) => { // 发送网络请求，获取图片数据
      if (error) {
        console.error(`request image failed: url: ${url}, code: ${error.code}, message: ${error.message}`); // 如果请求失败，输出错误信息
      } else {
        let imgData: ArrayBuffer = data.result as ArrayBuffer; // 将返回的数据转换为ArrayBuffer格式
        console.info(`request image success, size: ${imgData.byteLength}`); // 输出图片数据的大小
        let imgSource: image.ImageSource = image.createImageSource(imgData); // 创建ImageSource对象
        class sizeTmp {
          height: number = 100
          width: number = 100
        }
        let options: Record<string, number | boolean | sizeTmp> = { // 定义图片解码选项
          'alphaType': 0, // 透明度类型
          'editable': false, // 是否可编辑
          'pixelFormat': 3, // 像素格式
          'scaleMode': 1, // 缩放模式
          'size': { height: 100, width: 100 } // 图片尺寸
        }
        imgSource.createPixelMap(options).then((pixelMap: PixelMap) => { // 将ImageSource解码为PixelMap
          console.error('image createPixelMap success'); // 输出解码成功信息
          this.pixelMapImg = pixelMap; // 将解码后的PixelMap赋值给状态变量
        })
      }
    })
  }

  build() {
    Column() { // 使用Column布局
      Image(this.pixelMapImg) // 创建Image组件，显示加载的图片
        .alt($r('app.media.img')) // 设置占位图，当图片加载失败或加载过程中显示
        .objectFit(ImageFit.None) // 设置图片适应模式为None，保持原始尺寸
        .width('100%') // 设置图片宽度为100%
        .height('100%') // 设置图片高度为100%
    }
  }
}

@Entry
@Component
struct ImageExample {
  build() {
    Column() {
      Flex({ direction: FlexDirection.Column, alignItems: ItemAlign.Start }) {
        Row() {
          // 第一张图像，资源名为 app.media.sky，宽度为 110vp，高度为 110vp，外边距为 15vp，边框宽度为 2vp，颜色为粉色，objectFit 属性设置为 ImageFit.TOP_START
          Image($r('app.media.sky'))
            .width(110).height(110).margin(15)
            .overlay('png', { align: Alignment.Bottom, offset: { x: 0, y: 20 } })
            .border({ width: 2, color: Color.Pink })
            .objectFit(ImageFit.TOP_START)

          // 第二张图像，资源名为 app.media.loading，宽度为 110vp，高度为 110vp，外边距为 15vp，边框宽度为 2vp，颜色为粉色，objectFit 属性设置为 ImageFit.BOTTOM_START
          Image($r('app.media.loading'))
            .width(110).height(110).margin(15)
            .overlay('gif', { align: Alignment.Bottom, offset: { x: 0, y: 20 } })
            .border({ width: 2, color: Color.Pink })
            .objectFit(ImageFit.BOTTOM_START)
        }
        Row() {
          // 第三张图像，资源名为 app.media.svg，宽度为 110vp，高度为 110vp，外边距为 15vp，边框宽度为 2vp，颜色为粉色，objectFit 属性设置为 ImageFit.TOP_END
          Image($r('app.media.svg'))
            .width(110).height(110).margin(15)
            .overlay('svg', { align: Alignment.Bottom, offset: { x: 0, y: 20 } })
            .border({ width: 2, color: Color.Pink })
            .objectFit(ImageFit.TOP_END)

          // 第四张图像，资源名为 app.media.jpg，宽度为 110vp，高度为 110vp，外边距为 15vp，边框宽度为 2vp，颜色为粉色，objectFit 属性设置为 ImageFit.CENTER
          Image($r('app.media.jpg'))
            .width(110).height(110).margin(15)
            .overlay('jpg', { align: Alignment.Bottom, offset: { x: 0, y: 20 } })
            .border({ width: 2, color: Color.Pink })
            .objectFit(ImageFit.CENTER)
        }
      }
    }.height(320).width(360).padding({ right: 10, top: 10 })
  }
}

