@Component
struct ListExample {
  private arr: number[] = [] // 用于存储列表项数据的数组
  private scrollerForList: Scroller = new Scroller() // 用于控制列表滚动的 Scroller 对象

  aboutToAppear() {
    for (let i = 0; i < 20; i++) {
      this.arr.push(i) // 初始化列表项数据，添加 20 个数字到数组中
    }
  }

  build() {
    Column() {
      Row() {
        List({ space: 20, initialIndex: 3, scroller: this.scrollerForList }) {
          ForEach(this.arr, (item: number) => {
            ListItem() {
              Text('' + item) // 显示列表项的数字
                .width('100%').height(100).fontSize(16) // Text 组件的宽度、高度和字体大小
                .textAlign(TextAlign.Center) // 文本居中对齐
            }
            .borderRadius(10).backgroundColor(0xFFFFFF) // 列表项的边框圆角和背景颜色
            .width('60%') // 列表项的宽度
            .height('80%') // 列表项的高度
          }, (item: number) => JSON.stringify(item)) // 列表项的唯一标识符
        }
        .chainAnimation(true) // 启用列表项的链式动画
        .edgeEffect(EdgeEffect.Spring) // 启用弹性边缘效果
        .listDirection(Axis.Horizontal) // 设置列表为水平方向
        .height('100%') // 列表的高度
        .width('100%') // 列表的宽度
        .scrollSnapAlign(ScrollSnapAlign.CENTER) // 设置滚动对齐方式为居中对齐
        .borderRadius(10) // 列表的边框圆角
        .backgroundColor(0xDCDCDC) // 列表的背景颜色
      }
      .width('100%') // Row 组件的宽度
      .height('100%') // Row 组件的高度
      .backgroundColor(0xDCDCDC) // Row 组件的背景颜色
      .padding({ top: 10 }) // Row 组件的顶部内边距
    }
  }
}

