                  .backgroundColor('#3366CC') // 设置 Text 组件的背景颜色为 #3366CC
                  .borderRadius(15) // 设置 Text 组件的边框圆角为 15vp
                  .fontSize(16) // 设置 Text 组件的字体大小为 16
                  .textAlign(TextAlign.Center) // 设置 Text 组件的文本居中对齐
                  .margin({ top: 5 }) // 设置 Text 组件的上边距为 5vp
              }
            }, (item: number) => item.toString()) // 使用数字的字符串形式作为唯一标识符
          }.margin({ right: 15 }) // 设置 Flex 组件的右边距为 15vp
        }
