@Entry
@Component
struct MenuExample {
  @State selected: number = 0; // 用于记录当前选中的菜单项索引

  build() {
    Flex({ direction: FlexDirection.Column, alignItems: ItemAlign.Start, justifyContent: FlexAlign.SpaceBetween }) {
      Text('Menu').fontSize(20).fontColor(Color.Black).margin({ top: 10, bottom: 10 }) // 标题文本，字体大小为20，颜色为黑色，上下边距为10vp

      Menu() {
        MenuItemGroup({ header: 'Group1' }) { // 菜单项组1，标题为"Group1"
          MenuItem({ content: 'Item1' }) // 菜单项1，内容为"Item1"
          MenuItem({ content: 'Item2' }) // 菜单项2，内容为"Item2"
        }
        MenuItemGroup({ header: 'Group2' }) { // 菜单项组2，标题为"Group2"
          MenuItem({ content: 'Item3' }) // 菜单项3，内容为"Item3"
          MenuItem({ content: 'Item4' }) // 菜单项4，内容为"Item4"
        }
      }
      .width('100%') // 菜单宽度占据父容器的100%
      .height(200) // 菜单高度为200vp
      .backgroundColor(Color.White) // 菜单背景颜色为白色
      .borderRadius(10) // 菜单边框圆角为10vp
      .padding(10) // 菜单内边距为10vp
      .onSelect((value: number) => { // 菜单项选中事件
        this.selected = value; // 更新选中状态
        console.log(`Selected: ${value}`); // 打印选中项的索引
      })

      Text(`Selected: ${this.selected}`).fontSize(16).fontColor(Color.Gray).margin({ top: 20 }) // 显示当前选中的菜单项索引，字体大小为16，颜色为灰色，上边距为20vp
    }
    .width('100%') // 整体布局宽度占据父容器的100%
    .height(400) // 整体布局高度为400vp
    .padding({ left: 35, right: 35, top: 35 }) // 整体布局内边距为左右35vp、顶部35vp
    .backgroundColor(Color.LightGray) // 整体布局背景颜色为浅灰色
  }
}

Search.code:
@Entry
@Component
struct Index {
  @State text: string = 'Search editMenuOptions' // 定义一个状态变量 text，初始值为 'Search editMenuOptions'

  // 定义 onCreateMenu 方法，用于自定义菜单项的内容、图标和回调方法
  onCreateMenu(menuItems: Array<TextMenuItem>) {
    menuItems.forEach((value, index) => {
      value.icon = $r('app.media.startIcon') // 为每个菜单项设置图标，图标资源名为 'app.media.startIcon'
      if (value.id.equals(TextMenuItemId.COPY)) {
        value.content = "复制change" // 如果菜单项的 id 为 COPY，则修改其内容为 "复制change"
      }
      if (value.id.equals(TextMenuItemId.SELECT_ALL)) {
        value.content = "全选change" // 如果菜单项的 id 为 SELECT_ALL，则修改其内容为 "全选change"
      }
    })
    let item1: TextMenuItem = {
      content: 'custom1', // 定义一个自定义菜单项，内容为 'custom1'
      icon: $r('app.media.startIcon'), // 设置图标，图标资源名为 'app.media.startIcon'
      id: TextMenuItemId.of('custom1'), // 设置菜单项的 id 为 'custom1'
    }
    let item2: TextMenuItem = {
      content: 'custom2', // 定义一个自定义菜单项，内容为 'custom2'
      id: TextMenuItemId.of('custom2'), // 设置菜单项的 id 为 'custom2'
      icon: $r('app.media.startIcon'), // 设置图标，图标资源名为 'app.media.startIcon'
    }
    menuItems.push(item1) // 将自定义菜单项 item1 添加到菜单项列表的末尾
    menuItems.unshift(item2) // 将自定义菜单项 item2 添加到菜单项列表的开头
    return menuItems // 返回修改后的菜单项列表
  }

  build() {
    Column() {
      Search({ value: this.text }) // 创建一个 Search 组件，绑定状态变量 text
        .width('95%') // 设置 Search 组件的宽度为 95%
        .editMenuOptions({
          onCreateMenu: this.onCreateMenu, // 设置 onCreateMenu 回调方法，用于自定义菜单项
          onMenuItemClick: (menuItem: TextMenuItem, textRange: TextRange) => { // 设置 onMenuItemClick 回调方法，用于处理菜单项点击事件
            if (menuItem.id.equals(TextMenuItemId.of("custom2"))) {
              console.log("拦截 id: custom2 start:" + textRange.start + "; end:" + textRange.end) // 如果点击的菜单项 id 为 'custom2'，则拦截并输出日志
              return true; // 返回 true 表示拦截该点击事件
            }
            if (menuItem.id.equals(TextMenuItemId.COPY)) {
              console.log("拦截 COPY start:" + textRange.start + "; end:" + textRange.end) // 如果点击的菜单项 id 为 COPY，则拦截并输出日志
              return true; // 返回 true 表示拦截该点击事件
            }
            if (menuItem.id.equals(TextMenuItemId.SELECT_ALL)) {
              console.log("不拦截 SELECT_ALL start:" + textRange.start + "; end:" + textRange.end) // 如果点击的菜单项 id 为 SELECT_ALL，则不拦截并输出日志
              return false; // 返回 false 表示不拦截该点击事件
            }
            return false; // 默认返回 false，表示不拦截其他菜单项的点击事件
          }
        })
        .margin({ top: 100 }) // 设置 Search 组件的顶部外边距为 100vp
    }
    .width("90%") // 设置 Column 组件的宽度为 90%
    .margin("5%") // 设置 Column 组件的左右外边距为 5%
  }
}

