      Column() {
        // 显示标题文本，字体大小为9，颜色为浅灰色，宽度为90%，内边距为10
        Text('Text组件getLayoutManager接口获取段落相对组件的信息')
          .fontSize(9)
          .fontColor(0xCCCCCC)
          .width('90%')
          .padding(10)

        // 显示文本内容，字体大小为25，边框宽度为1，当文本区域发生变化时获取行数
        Text(this.textStr, { controller: this.controller })
          .fontSize(25)
          .borderWidth(1)
          .onAreaChange(() => {
            let layoutManager: LayoutManager = this.controller.getLayoutManager()
            this.lineCount = "LineCount: " + layoutManager.getLineCount()
          })

        // 显示行数标题，字体大小为9，颜色为浅灰色，宽度为90%，内边距为10
        Text('LineCount').fontSize(9).fontColor(0xCCCCCC).width('90%').padding(10)

        // 显示行数信息
        Text(this.lineCount)

        // 显示字形位置标题，字体大小为9，颜色为浅灰色，宽度为90%，内边距为10
        Text('GlyphPositionAtCoordinate').fontSize(9).fontColor(0xCCCCCC).width('90%').padding(10)

        // 显示按钮，点击按钮时获取相对组件坐标[150,50]的字形信息
        Button("相对组件坐标[150,50]字形信息")
          .onClick(() => {
            let layoutManager: LayoutManager = this.controller.getLayoutManager()
            let position: PositionWithAffinity = layoutManager.getGlyphPositionAtCoordinate(150, 50)
            this.glyphPositionAtCoordinate =
              "相对组件坐标[150,50] glyphPositionAtCoordinate position: " + position.position + " affinity: " +
              position.affinity
          })
          .margin({ bottom: 20, top: 10 })

        // 显示字形位置信息
        Text(this.glyphPositionAtCoordinate)

        // 显示行度量标题，字体大小为9，颜色为浅灰色，宽度为90%，内边距为10
        Text('LineMetrics').fontSize(9).fontColor(0xCCCCCC).width('90%').padding(10)

        // 显示按钮，点击按钮时获取首行行信息、文本样式信息、以及字体属性信息
        Button("首行行信息、文本样式信息、以及字体属性信息")
          .onClick(() => {
            let layoutManager: LayoutManager = this.controller.getLayoutManager()
            let lineMetrics: LineMetrics = layoutManager.getLineMetrics(0)
            this.lineMetrics = "lineMetrics is " + JSON.stringify(lineMetrics) + '\\n\\n'
            let runMetrics = lineMetrics.runMetrics
            runMetrics.forEach((value, key) => {
              this.lineMetrics += "runMetrics key is " + key + " " + JSON.stringify(value) + "\\n\\n"
            });
          })
          .margin({ bottom: 20, top: 10 })

        // 显示行度量信息
        Text(this.lineMetrics)
      }
      .margin({ top: 100, left: 8, right: 8 })
    }
  }
}

@Entry
@Component
struct TextExample12 {
  build() {
    Column({ space: 10 }) { // 创建一个垂直布局，子组件之间的间距为 10vp

      Text('halfLeading') // 创建一个 Text 组件，显示标题 "halfLeading"
        .fontSize(9) // 设置字体大小为 9vp
        .fontColor(0xCCCCCC) // 设置字体颜色为灰色（0xCCCCCC）
        .margin(15) // 设置外边距为 15vp
        .width('90%') // 设置宽度为父容器的 90%

      Text("This is the text with the halfLeading set.") // 创建一个 Text 组件，显示文本 "This is the text with the halfLeading set."
        .lineHeight(60) // 设置行高为 60vp
        .halfLeading(true) // 启用 halfLeading 属性，行间距减半
        .borderWidth(1) // 设置边框宽度为 1vp
        .width('80%') // 设置宽度为父容器的 80%

      Text("This is the text without the halfLeading set.") // 创建一个 Text 组件，显示文本 "This is the text without the halfLeading set."
        .lineHeight(60) // 设置行高为 60vp
        .halfLeading(false) // 禁用 halfLeading 属性，行间距保持默认值
        .borderWidth(1) // 设置边框宽度为 1vp
        .width('80%') // 设置宽度为父容器的 80%
    }
  }
}

