@Entry
@Component
struct TextExample7 {
  // 创建一个TextController实例，用于控制文本的选择和菜单的显示
  controller: TextController = new TextController();
  // 创建一个TextOptions实例，用于配置文本的控制器
  options: TextOptions = { controller: this.controller };

  build() {
    Column() {
      Column() {
        // 创建一个Text组件，内容为undefined，使用options配置
        Text(undefined, this.options) {
          // 创建一个Span组件，内容为"Hello World"
          Span('Hello World')
          // 创建一个ImageSpan组件，内容为应用内的图标资源
          ImageSpan($r('app.media.icon'))
            .width('100px') // 设置图像宽度为100px
            .height('100px') // 设置图像高度为100px
            .objectFit(ImageFit.Fill) // 设置图像填充方式为完全填充
            .verticalAlign(ImageSpanAlignment.CENTER) // 设置图像垂直对齐方式为居中
        }
        .copyOption(CopyOptions.InApp) // 设置文本的复制选项为仅在应用内复制
        .bindSelectionMenu(TextSpanType.IMAGE, this.LongPressImageCustomMenu, TextResponseType.LONG_PRESS, {
          onDisappear: () => {
            console.info(`自定义选择菜单关闭时回调`); // 自定义选择菜单关闭时的回调函数
          },
          onAppear: () => {
            console.info(`自定义选择菜单弹出时回调`); // 自定义选择菜单弹出时的回调函数
          }
        })
        .bindSelectionMenu(TextSpanType.TEXT, this.RightClickTextCustomMenu, TextResponseType.RIGHT_CLICK) // 绑定文本的右键点击菜单
        .bindSelectionMenu(TextSpanType.MIXED, this.SelectMixCustomMenu, TextResponseType.SELECT) // 绑定混合内容的选中菜单
        .onTextSelectionChange((selectionStart: number, selectionEnd: number) => {
          console.info(`文本选中区域变化回调, selectionStart: ${selectionStart}, selectionEnd: ${selectionEnd}`); // 文本选中区域变化时的回调函数
        })
        .borderWidth(1) // 设置边框宽度为1vp
        .borderColor(Color.Red) // 设置边框颜色为红色
        .width(200) // 设置宽度为200vp
        .height(100) // 设置高度为100vp
      }
      .width('100%') // 设置宽度为100%
      .backgroundColor(Color.White) // 设置背景颜色为白色
      .alignItems(HorizontalAlign.Start) // 设置对齐方式为左对齐
      .padding(25) // 设置内边距为25vp
    }
    .height('100%') // 设置高度为100%
  }

  // 定义右键点击文本时的自定义菜单
  @Builder
  RightClickTextCustomMenu() {
    Column() {
      Menu() {
        MenuItemGroup() {
          MenuItem({ startIcon: $r('app.media.app_icon'), content: "Right Click Menu 1", labelInfo: "" })
