import {AnimationOptions, AnimatedDrawableDescriptor} from '@kit.ArkUI' // 导入动画相关的接口和类
import { image } from '@kit.ImageKit' // 导入图像处理相关的接口和类

@Entry
@Component
struct ImageExample {
  pixelmaps: Array<PixelMap\>  = \[\]; // 定义一个PixelMap数组，用于存储动画帧
  options: AnimationOptions = {duration:2000, iterations:1}; // 定义动画选项，设置动画持续时间和播放次数
  @State animated: AnimatedDrawableDescriptor | undefined = undefined; // 定义一个状态变量，用于存储动画描述符

  async aboutToAppear() {
    this.pixelmaps = await this.getPixelMaps(); // 异步获取PixelMap数组
    this.animated = new AnimatedDrawableDescriptor(this.pixelmaps, this.options); // 创建动画描述符
  }

  build() {
    Column() {
      Row() {
        Image(this.animated) // 创建一个Image组件，展示动画
          .width('500px').height('500px') // 设置Image组件的宽度和高度
          .onFinish(() =\> { // 设置动画播放完成后的回调函数
            console.info("finish") // 输出"finish"到控制台
          })
      }.height('50%') // 设置Row组件的高度为屏幕的50%
      Row() {
        Button('once').width(100).padding(5).onClick(() =\> { // 创建一个按钮，设置宽度、内边距和点击事件
          this.options = {duration:2000, iterations:1}; // 设置动画选项，播放一次
          this.animated = new AnimatedDrawableDescriptor(this.pixelmaps, this.options); // 更新动画描述符
        }).margin(5) // 设置按钮的外边距
        Button('infinite').width(100).padding(5).onClick(() =\> { // 创建另一个按钮，设置宽度、内边距和点击事件
          this.options = {duration:2000, iterations:-1}; // 设置动画选项，无限循环播放
          this.animated = new AnimatedDrawableDescriptor(this.pixelmaps, this.options); // 更新动画描述符
        }).margin(5) // 设置按钮的外边距
      }
    }.width('50%') // 设置Column组件的宽度为屏幕的50%
  }

  private async getPixmapListFromMedia(resource: Resource) {
    let unit8Array = await getContext(this)?.resourceManager?.getMediaContent({ // 异步获取媒体内容
      bundleName: resource.bundleName,
      moduleName: resource.moduleName,
      id: resource.id
    })
    let imageSource = image.createImageSource(unit8Array.buffer.slice(0, unit8Array.buffer.byteLength)) // 创建图像源
    let createPixelMap: Array<image.PixelMap\> = await imageSource.createPixelMapList({ // 异步创建PixelMap数组
      desiredPixelFormat: image.PixelMapFormat.RGBA\_8888
    })
    await imageSource.release() // 释放图像源
    return createPixelMap // 返回PixelMap数组
  }

  private async getPixmapFromMedia(resource: Resource) {
    let unit8Array = await getContext(this)?.resourceManager?.getMediaContent({ // 异步获取媒体内容
      bundleName: resource.bundleName,
      moduleName: resource.moduleName,
      id: resource.id
    })
    let imageSource = image.createImageSource(unit8Array.buffer.slice(0, unit8Array.buffer.byteLength)) // 创建图像源
    let createPixelMap: image.PixelMap = await imageSource.createPixelMap({ // 异步创建PixelMap
      desiredPixelFormat: image.PixelMapFormat.RGBA\_8888
    })
    await imageSource.release() // 释放图像源
    return createPixelMap // 返回PixelMap
  }

  private async getPixelMaps() {
    let Mypixelmaps:Array<PixelMap\> = await this.getPixmapListFromMedia($r('app.media.view')) // 异步获取PixelMap数组
    Mypixelmaps.push(await this.getPixmapFromMedia($r('app.media.icon'))) // 异步获取PixelMap并添加到数组中
    return Mypixelmaps; // 返回PixelMap数组
  }
}

