        .margin({ top: '8vp', bottom: '8vp', left: '8vp', right: '8vp' })
        .objectFit(ImageFit.Contain)
        .alignRules({
          center: { anchor: '__container__', align: VerticalAlign.Center },
          middle: { anchor: '__container__', align: HorizontalAlign.Center },
          top: { anchor: '__container__', align: VerticalAlign.Top, offset: -0.1 * 150 }
        })

      // 2. Title TextView
      Text($r('app.string.app_name'))
        .id('grocery')
        .width('auto')
        .height('auto')
        .margin({ left: '8vp', right: '8vp' })
        .fontColor($r('app.color.color_black_light'))
        .fontSize('32fp')
        .fontWeight(FontWeight.Bold)
        .alignRules({
          top: { anchor: 'logo', align: VerticalAlign.Bottom },
          left: { anchor: '__container__', align: HorizontalAlign.Start },
          right: { anchor: '__container__', align: HorizontalAlign.End }
        })

      // 3. Slogan TextView
      Text($r('app.string.slogan'))
        .id('slogan')
        .width('auto')
        .height('auto')
        .margin({ top: '8vp', left: '8vp', right: '8vp' })
        .fontColor($r('app.color.color_black'))
        .fontSize('14fp')
        .alignRules({
          bottom: { anchor: '__container__', align: VerticalAlign.Bottom },
          left: { anchor: '__container__', align: HorizontalAlign.Start },
          right: { anchor: '__container__', align: HorizontalAlign.End }
        })

      // 4. Animation LottieAnimationView
      Image($r('app.media.loading'))
        .id('animation_view')
        .width('250vp')
        .height('250vp')
        .margin({ top: '8vp', bottom: '8vp', left: '8vp', right: '8vp' })
        .alignRules({
          center: { anchor: '__container__', align: VerticalAlign.Center },
          middle: { anchor: '__container__', align: HorizontalAlign.Center },
          verticalBias: { anchor: '__container__', align: VerticalAlign.Center, offset: 0.7 }
        })
        .autoPlay(true)
        .loop(true)
    }
    .width('100%')
    .height('100%')
