RelativeContainer.code:
@Entry
@Component
struct Index {
  build() {
    Row() {
      RelativeContainer() {
        // 第一个Row组件，包含一个Text组件，内容为'row1'，宽度为100，高度为100，背景颜色为红色，居中对齐
        Row(){Text('row1')}.justifyContent(FlexAlign.Center)
          .width(100).height(100)
          .backgroundColor("#FF3333")
          .id("row1") // 设置id为"row1"，用于后续的相对定位

        // 第二个Row组件，包含一个Text组件，内容为'row2'，宽度为100，高度为100，背景颜色为黄色，相对于"row1"进行定位
        Row(){Text('row2')}.justifyContent(FlexAlign.Center)
          .width(100).height(100)
          .backgroundColor("#FFCC00")
          .alignRules({
            left: {anchor: "row1", align: HorizontalAlign.End}, // 相对于"row1"的右边
            top: {anchor: "row1", align: VerticalAlign.Top} // 相对于"row1"的顶部
          })
          .id("row2") // 设置id为"row2"，用于后续的相对定位

        // 第三个Row组件，包含一个Text组件，内容为'row3'，宽度为100，高度为100，背景颜色为橙色，相对于"row1"进行定位
        Row(){Text('row3')}.justifyContent(FlexAlign.Center)
          .width(100).height(100)
          .backgroundColor("#FF6633")
          .alignRules({
            left: {anchor: "row1", align: HorizontalAlign.Start}, // 相对于"row1"的左边
            top: {anchor: "row1", align: VerticalAlign.Bottom} // 相对于"row1"的底部
          })
          .id("row3") // 设置id为"row3"，用于后续的相对定位

        // 第四个Row组件，包含一个Text组件，内容为'row4'，宽度为100，高度为100，背景颜色为浅橙色，相对于"row3"和"row2"进行定位
        Row(){Text('row4')}.justifyContent(FlexAlign.Center)
          .width(100).height(100)
          .backgroundColor("#FF9966")
          .alignRules({
            left: {anchor: "row3", align: HorizontalAlign.End}, // 相对于"row3"的右边
            top: {anchor: "row2", align: VerticalAlign.Bottom} // 相对于"row2"的底部
          })
          .id("row4") // 设置id为"row4"，用于后续的相对定位
      }
      .width("auto").height("auto") // 设置RelativeContainer的宽度和高度为"auto"，使其大小适应内容
      .margin({left: 50}) // 设置左边距为50
      .border({width:2, color: "#6699FF"}) // 设置边框宽度为2，颜色为蓝色
    }
    .height('100%') // 设置Row的高度为100%
  }
}

@Entry
@Component
struct Index {
  build() {
    Row() {
      RelativeContainer() {
        // 第一个Row组件，包含文本'row1'，设置为居中对齐，宽度80，高度80，背景色为红色
        Row(){Text('row1')}.justifyContent(FlexAlign.Center)
          .width(80).height(80)
          .backgroundColor("#FF3333")
          // 设置alignRules，定义组件的相对位置和对齐方式
          .alignRules({
            left: {anchor: "__container__", align: HorizontalAlign.Start}, // 左对齐到容器左侧
            right: {anchor: "row2", align : HorizontalAlign.Start}, // 右对齐到row2的左侧
            center: {anchor: "__container__", align: VerticalAlign.Center}, // 垂直居中对齐到容器
            bias : {horizontal : 0} // 水平方向的bias值为0
          })
          .id("row1") // 设置组件ID为row1
          .chainMode(Axis.Horizontal, ChainStyle.PACKED) // 设置水平方向的链模式为PACKED

        // 第二个Row组件，包含文本'row2'，设置为居中对齐，宽度80，高度80，背景色为黄色
        Row(){Text('row2')}.justifyContent(FlexAlign.Center)
          .width(80).height(80)
          .backgroundColor("#FFCC00")
          // 设置alignRules，定义组件的相对位置和对齐方式
          .alignRules({
            left: {anchor: "row1", align: HorizontalAlign.End}, // 左对齐到row1的右侧
            right: {anchor: "row3", align : HorizontalAlign.Start}, // 右对齐到row3的左侧
            top: {anchor: "row1", align: VerticalAlign.Top} // 顶部对齐到row1的顶部
          })
          .id("row2") // 设置组件ID为row2

        // 第三个Row组件，包含文本'row3'，设置为居中对齐，宽度80，高度80，背景色为橙色
        Row(){Text('row3')}.justifyContent(FlexAlign.Center)
          .width(80).height(80)
          .backgroundColor("#FF6633")
          // 设置alignRules，定义组件的相对位置和对齐方式
          .alignRules({
            left: {anchor: "row2", align: HorizontalAlign.End}, // 左对齐到row2的右侧
            right: {anchor: "__container__", align : HorizontalAlign.End}, // 右对齐到容器右侧
            top: {anchor: "row1", align: VerticalAlign.Top} // 顶部对齐到row1的顶部
          })
          .id("row3") // 设置组件ID为row3
      }
      .width(300).height(300) // 设置RelativeContainer的宽度和高度
      .margin({left: 50}) // 设置左侧外边距
      .border({width:2, color: "#6699FF"}) // 设置边框宽度和颜色
    }
    .height('100%') // 设置Row的高度为100%
  }
}

@Entry
@Component
struct Index {
  build() {
    Row() {
      RelativeContainer() {
        // 第一个矩形，红色背景，位于容器的左上角，设置了10像素的margin
        Row(){Text('row1')}.justifyContent(FlexAlign.Center)
          .width(100).height(100)
          .backgroundColor("#FF3333")
          .alignRules({
            top: {anchor: "__container__", align: VerticalAlign.Top}, // 顶部对齐容器顶部
            left: {anchor: "__container__", align: HorizontalAlign.Start} // 左侧对齐容器左侧
          })
          .id("row1")
          .margin(10) // 设置外边距为10像素

        // 第二个矩形，黄色背景，位于第一个矩形的右侧
        Row(){Text('row2')}.justifyContent(FlexAlign.Center)
          .width(100).height(100)
          .backgroundColor("#FFCC00")
          .alignRules({
            left: {anchor: "row1", align: HorizontalAlign.End}, // 左侧对齐第一个矩形的右侧
            top: {anchor: "row1", align: VerticalAlign.Top} // 顶部对齐第一个矩形的顶部
          })
          .id("row2")

        // 第三个矩形，橙色背景，位于第一个矩形的下方
        Row(){Text('row3')}.justifyContent(FlexAlign.Center)
          .width(100).height(100)
          .backgroundColor("#FF6633")
          .alignRules({
            left: {anchor: "row1", align: HorizontalAlign.Start}, // 左侧对齐第一个矩形的左侧
            top: {anchor: "row1", align: VerticalAlign.Bottom} // 顶部对齐第一个矩形的底部
          })
          .id("row3")

        // 第四个矩形，浅橙色背景，位于第三个矩形的右侧，第二个矩形的下方，设置了10像素的margin
        Row(){Text('row4')}.justifyContent(FlexAlign.Center)
          .width(100).height(100)
          .backgroundColor("#FF9966")
          .alignRules({
            left: {anchor: "row3", align: HorizontalAlign.End}, // 左侧对齐第三个矩形的右侧
            top: {anchor: "row2", align: VerticalAlign.Bottom} // 顶部对齐第二个矩形的底部
          })
          .id("row4")
          .margin(10) // 设置外边距为10像素
      }
      .width(300).height(300) // 设置RelativeContainer的宽度和高度
      .margin({left: 50}) // 设置RelativeContainer的左侧外边距为50像素
      .border({width:2, color: "#6699FF"}) // 设置RelativeContainer的边框
    }
    .height('100%') // 设置Row的高度为100%
  }
}

