RelativeContainer.code:
@Entry
@Component
struct Index {
  build() {
    Row() {
      RelativeContainer() {
        // 创建一个宽度为100，高度为100的红色矩形
        Row().width(100).height(100)
          .backgroundColor("#FF3333")
          .alignRules({
            // 矩形的顶部与父容器的顶部对齐
            top: {anchor: "__container__", align: VerticalAlign.Top},
            // 矩形的底部与父容器的底部对齐
            bottom: {anchor: "__container__", align: VerticalAlign.Bottom},
            // 矩形的左侧与父容器的左侧对齐
            left: {anchor: "__container__", align: HorizontalAlign.Start},
            // 矩形的右侧与父容器的右侧对齐
            right: {anchor: "__container__", align: HorizontalAlign.End},
            // 设置矩形的垂直偏移，使其相对于父容器的顶部和底部偏移30%
            bias: {vertical: 0.3}
          })
          .id("row1")
      }
      .width(300).height(300)
      .margin({left: 50}) // 设置RelativeContainer的左边距为50
      .border({width:2, color: "#6699FF"}) // 为RelativeContainer添加一个宽度为2的蓝色边框
    }
    .height('100%') // 设置Row的高度为100%
  }
}

Text.code:
@Entry
@Component
struct TextExample12 {
  build() {
    Column({ space: 10 }) { // 创建一个垂直布局，子组件之间的间距为 10vp

      Text('halfLeading') // 创建一个 Text 组件，显示标题 "halfLeading"
        .fontSize(9) // 设置字体大小为 9vp
        .fontColor(0xCCCCCC) // 设置字体颜色为灰色（0xCCCCCC）
        .margin(15) // 设置外边距为 15vp
        .width('90%') // 设置宽度为父容器的 90%

      Text("This is the text with the halfLeading set.") // 创建一个 Text 组件，显示文本 "This is the text with the halfLeading set."
        .lineHeight(60) // 设置行高为 60vp
        .halfLeading(true) // 启用 halfLeading 属性，行间距减半
        .borderWidth(1) // 设置边框宽度为 1vp
        .width('80%') // 设置宽度为父容器的 80%

      Text("This is the text without the halfLeading set.") // 创建一个 Text 组件，显示文本 "This is the text without the halfLeading set."
        .lineHeight(60) // 设置行高为 60vp
        .halfLeading(false) // 禁用 halfLeading 属性，行间距保持默认值
        .borderWidth(1) // 设置边框宽度为 1vp
        .width('80%') // 设置宽度为父容器的 80%
    }
  }
}

