          .backgroundColor($r('app.color.default_background'))
          .shadow({ radius: 5 })

        // 约束布局
        Column() {
          Image($r('app.media.b1'))
            .width('100%')
            .height('100%')
            .objectFit(ImageFit.Cover)

          // 文本视图
          Column() {
            Text('20/04/1997')
              .id('order_date')
              .width('75vp')
              .height('16vp')
              .margin({ left: '8vp', top: '8vp' })
              .fontColor($r('app.color.white'))
              .fontWeight(FontWeight.Bold)
              .align(Alignment.Start)
              .verticalAlign(VerticalAlign.Top)

            Text('20:18:35')
              .id('order_time')
              .width('auto')
              .height('auto')
              .margin({ right: '8vp' })
              .fontColor($r('app.color.white'))
              .align(Alignment.End)
              .verticalAlign(VerticalAlign.Baseline)
          }
          .width('100%')
          .height('auto')
          .padding('16vp')
          .backgroundColor($r('app.media.custom_background'))

          // 水平线性容器
          Row() {
            // 第一个水平线性容器
          }
          .width('100%')
          .height('1vp')
          .backgroundColor('#ec526576')
          .margin({ top: '32vp' })

          RelativeContainer() {
            Row() {
              // 第二个水平线性容器
            }
            .width('100%')
            .height('1vp')
            .backgroundColor('#ec526576')
            .alignRules({
              bottom: { anchor: '__container__', align: VerticalAlign.Bottom }
            })
          }
          .width('100%')
          .height('100%')
          .alignRules({
            bottom: { anchor: '__container__', align: VerticalAlign.Bottom, offset: 0.82 }
          })

          // 文本视图
          RelativeContainer() {
            Text('OrderId')
              .id('orderIdText')
              .width('auto')
              .height('auto')
              .margin({ left: '8vp' })
              .fontColor('#8795bb')
              .fontFamily('HarmonyOS Sans')
              .alignRules({
                top: { anchor: '__container__', align: VerticalAlign.Top },
                left: { anchor: '__container__', align: HorizontalAlign.Start }
              })

            Text('Status')
              .id('statustText')
              .width('auto')
              .height('17vp')
              .margin({ left: '8vp' })
              .fontColor('#8795bb')
              .fontFamily('HarmonyOS Sans')
              .alignRules({
                top: { anchor: 'orderIdText', align: VerticalAlign.Bottom },
                left: { anchor: '__container__', align: HorizontalAlign.Start }
              })

            Text('Total Savings')
              .id('totalSavingsText')
              .width('auto')
              .height('auto')
              .margin({ left: '8vp' })
              .fontColor('#8795bb')
              .fontFamily('HarmonyOS Sans')
              .alignRules({
                top: { anchor: 'statustText', align: VerticalAlign.Bottom },
                left: { anchor: '__container__', align: HorizontalAlign.Start }
              })

            Text('Payable Amount')
              .id('payableAmountText')
              .width('auto')
              .height('auto')
              .margin({ left: '8vp' })
              .fontColor('#8795bb')
              .fontFamily('HarmonyOS Sans')
              .alignRules({
                baseline: { anchor: 'payableAmount', align: VerticalAlign.Baseline },
                right: { anchor: 'payableAmount', align: HorizontalAlign.Start }
              })

            Text('TextView')
              .id('orderId')
              .width('auto')
              .height('auto')
              .fontColor('#FFFFFF')
              .fontFamily('HarmonyOS Sans')
              .alignRules({
                baseline: { anchor: 'orderIdText', align: VerticalAlign.Baseline },
                left: { anchor: 'status', align: HorizontalAlign.Start }
              })

            Text('TextView')
              .id('status')
              .width('auto')
              .height('auto')
              .fontColor('#FFFFFF')
              .fontFamily('HarmonyOS Sans')
              .alignRules({
                top: { anchor: 'orderId', align: VerticalAlign.Bottom },
                left: { anchor: 'total_savings', align: HorizontalAlign.Start }
              })

            Text('TextView')
              .id('total_savings')
              .width('auto')
