            left: {anchor: "row3", align: HorizontalAlign.End}, // 相对于"row3"的右边
            top: {anchor: "row2", align: VerticalAlign.Bottom} // 相对于"row2"的底部
          })
          .id("row4") // 设置id为"row4"，用于后续的相对定位
      }
      .width("auto").height("auto") // 设置RelativeContainer的宽度和高度为"auto"，使其大小适应内容
      .margin({left: 50}) // 设置左边距为50
      .border({width:2, color: "#6699FF"}) // 设置边框宽度为2，颜色为蓝色
    }
    .height('100%') // 设置Row的高度为100%
  }
}

