// 定义一个自定义样式类 `MyRadioStyle`，实现 `ContentModifier` 接口，用于修改单选按钮的样式
class MyRadioStyle implements ContentModifier<RadioConfiguration> {
  // 单选按钮的类型，用于区分不同样式的按钮
  type: number = 0
  // 单选按钮选中时的颜色
  selectedColor: ResourceColor = Color.Black

  // 构造函数，初始化单选按钮的类型和选中颜色
  constructor(numberType: number, colorType: ResourceColor) {
    this.type = numberType
    this.selectedColor = colorType
  }

  // 实现 `applyContent` 方法，返回一个 `WrappedBuilder`，用于构建单选按钮的内容
  applyContent(): WrappedBuilder<[RadioConfiguration]> {
    return wrapBuilder(buildRadio)
  }
}

// 定义一个 `@Builder` 函数 `buildRadio`，用于构建单选按钮的内容
@Builder
function buildRadio(config: RadioConfiguration) {
  // 使用 `Row` 组件创建一个水平布局，设置间距为 30
  Row({ space: 30 }) {
    // 创建一个圆形组件，设置宽度和高度为 50，边框颜色为黑色
    Circle({ width: 50, height: 50 })
      .stroke(Color.Black)
      // 根据单选按钮的选中状态，设置填充颜色为选中颜色或白色
      .fill(config.checked ? (config.contentModifier as MyRadioStyle).selectedColor : Color.White)
    // 创建一个按钮组件，设置按钮文本为 "off" 或 "on"，根据单选按钮的选中状态
    Button(config.checked ? "off" : "on")
      .width(100)
      // 根据单选按钮的选中状态，设置按钮类型
      .type(config.checked ? (config.contentModifier as MyRadioStyle).type : ButtonType.Normal)
      .backgroundColor('#2787D9')
      // 设置按钮的点击事件，切换单选按钮的选中状态
      .onClick(() => {
        if (config.checked) {
          config.triggerChange(false)
        } else {
          config.triggerChange(true)
        }
      })
  }
}

// 定义一个入口组件 `refreshExample`，使用 `@Entry` 和 `@Component` 装饰器
@Entry
@Component
struct refreshExample {
  // 实现 `build` 方法，构建组件的 UI
  build() {
    // 使用 `Column` 组件创建一个垂直布局，设置间距为 50
    Column({ space: 50 }) {
      // 创建一个水平布局的行
      Row() {
        // 创建一个单选按钮，设置值为 "Radio1"，组名为 "radioGroup"
        Radio({ value: 'Radio1', group: 'radioGroup' })
          // 设置单选按钮的样式为 `MyRadioStyle`，类型为 1，选中颜色为 "#004AAF"
          .contentModifier(new MyRadioStyle(1, '#004AAF'))
          // 设置单选按钮的初始选中状态为 false
          .checked(false)
          // 设置单选按钮的宽度为 300，高度为 100
          .width(300)
          .height(100)
      }

      // 创建另一个水平布局的行
      Row() {
        // 创建一个单选按钮，设置值为 "Radio2"，组名为 "radioGroup"
        Radio({ value: 'Radio2', group: 'radioGroup' })
          // 设置单选按钮的初始选中状态为 true
          .checked(true)
          // 设置单选按钮的宽度为 300，高度为 60
          .width(300)
          .height(60)
          // 设置单选按钮的样式为 `MyRadioStyle`，类型为 2，选中颜色为 "#004AAF"
          .contentModifier(new MyRadioStyle(2, '#004AAF'))
      }
    }
  }
}

