组件的功能包括自定义编辑菜单选项的文本内容、图标和回调方法。用户可以通过点击菜单项触发相应的操作，并在控制台输出相关信息。
@Entry
@Component
struct TextInputExample {
  @State text: string = 'TextInput editMenuOptions' // 初始化文本输入框的默认文本内容

  // 自定义菜单项的创建方法
  onCreateMenu(menuItems: Array<TextMenuItem>) {
    menuItems.forEach((value, index) => {
      value.icon = $r('app.media.startIcon') // 设置菜单项的图标
      if (value.id.equals(TextMenuItemId.COPY)) {
        value.content = "复制change" // 修改复制菜单项的文本内容
      }
      if (value.id.equals(TextMenuItemId.SELECT_ALL)) {
        value.content = "全选change" // 修改全选菜单项的文本内容
      }
    })
    let item1: TextMenuItem = {
      content: 'custom1', // 自定义菜单项1的文本内容
      icon: $r('app.media.startIcon'), // 自定义菜单项1的图标
      id: TextMenuItemId.of('custom1'), // 自定义菜单项1的唯一标识
    }
    let item2: TextMenuItem = {
      content: 'custom2', // 自定义菜单项2的文本内容
      id: TextMenuItemId.of('custom2'), // 自定义菜单项2的唯一标识
      icon: $r('app.media.startIcon'), // 自定义菜单项2的图标
    }
    menuItems.push(item1) // 将自定义菜单项1添加到菜单项列表的末尾
    menuItems.unshift(item2) // 将自定义菜单项2添加到菜单项列表的开头
    return menuItems // 返回修改后的菜单项列表
  }

  build() {
    Column() {
      TextInput({ text: this.text }) // 创建一个文本输入框，初始文本为@State变量text的值
        .width('95%') // 设置文本输入框的宽度为95%
        .height(50) // 设置文本输入框的高度为50vp
        .editMenuOptions({
          onCreateMenu: this.onCreateMenu, // 设置自定义菜单项的创建方法
          onMenuItemClick: (menuItem: TextMenuItem, textRange: TextRange) => { // 设置菜单项点击的回调方法
            if (menuItem.id.equals(TextMenuItemId.of("custom2"))) {
              console.log("拦截 id: custom2 start:" + textRange.start + "; end:" + textRange.end) // 拦截自定义菜单项2的点击事件，并输出相关信息
              return true; // 返回true表示拦截该事件
            }
            if (menuItem.id.equals(TextMenuItemId.COPY)) {
              console.log("拦截 COPY start:" + textRange.start + "; end:" + textRange.end) // 拦截复制菜单项的点击事件，并输出相关信息
              return true; // 返回true表示拦截该事件
            }
            if (menuItem.id.equals(TextMenuItemId.SELECT_ALL)) {
              console.log("不拦截 SELECT_ALL start:" + textRange.start + "; end:" + textRange.end) // 不拦截全选菜单项的点击事件，并输出相关信息
              return false; // 返回false表示不拦截该事件
            }
            return false; // 默认返回false，不拦截其他菜单项的点击事件
          }
        })
        .margin({ top: 100 }) // 设置文本输入框的上边距为100vp
    }
    .width("90%") // 设置垂直布局的宽度为90%
    .margin("5%") // 设置垂直布局的左右边距为5%
  }
}

