@Component
struct StickyNestedScroll {
  @State arr: number[] = [] // 定义一个状态变量 arr，用于存储列表项的数据

  @Styles
  listCard() {
    .backgroundColor(Color.White) // 设置背景颜色为白色
    .height(72) // 设置高度为 72vp
    .width("100%") // 设置宽度占据整个屏幕
    .borderRadius(12) // 设置边框圆角为 12vp
  }

  build() {
    Scroll() { // 创建一个滚动区域
      Column() { // 创建一个垂直布局的容器
        Text("Scroll Area") // 创建一个文本组件，显示“Scroll Area”
          .width("100%") // 设置宽度占据整个屏幕
          .height("40%") // 设置高度为 40%
          .backgroundColor('#0080DC') // 设置背景颜色为蓝色
          .textAlign(TextAlign.Center) // 设置文本居中对齐
        Tabs({ barPosition: BarPosition.Start }) { // 创建一个选项卡组件，标签栏位于内容区域的上方
          TabContent() { // 创建第一个标签页的内容区域
            List({ space: 10 }) { // 创建一个列表组件，列表项之间的间距为 10vp
              ForEach(this.arr, (item: number) => { // 通过 ForEach 循环生成列表项
                ListItem() { // 创建一个列表项
                  Text("item" + item) // 创建一个文本组件，显示列表项的内容
                    .fontSize(16) // 设置字体大小为 16
                }.listCard() // 应用 listCard 样式
              }, (item: string) => item) // 定义列表项的唯一标识符
            }.width("100%") // 设置列表组件的宽度占据整个屏幕
            .edgeEffect(EdgeEffect.Spring) // 设置列表组件的滚动效果为弹性滚动
            .nestedScroll({ // 设置列表组件的嵌套滚动模式
              scrollForward: NestedScrollMode.PARENT_FIRST, // 向前滚动时，优先滚动父容器
              scrollBackward: NestedScrollMode.SELF_FIRST // 向后滚动时，优先滚动自身
            })
          }.tabBar("Tab1") // 设置第一个标签页的标签名为“Tab1”

          TabContent() { // 创建第二个标签页的内容区域
          }.tabBar("Tab2") // 设置第二个标签页的标签名为“Tab2”
        }
        .vertical(false) // 设置选项卡组件的排列方式为水平排列
        .height("100%") // 设置选项卡组件的高度占据整个屏幕
      }.width("100%") // 设置垂直布局容器的宽度占据整个屏幕
    }
    .edgeEffect(EdgeEffect.Spring) // 设置滚动区域的滚动效果为弹性滚动
    .friction(0.6) // 设置滚动区域的滚动摩擦系数为 0.6
    .backgroundColor('#DCDCDC') // 设置滚动区域的背景颜色为浅灰色
    .scrollBar(BarState.Off) // 设置滚动区域的滚动条状态为关闭
    .width('100%') // 设置滚动区域的宽度占据整个屏幕
    .height('100%') // 设置滚动区域的高度占据整个屏幕
  }

  aboutToAppear() { // 组件即将显示时执行的回调函数
    for (let i = 0; i < 30; i++) { // 循环生成 30 个列表项的数据
      this.arr.push(i) // 将生成的数据添加到 arr 数组中
    }
  }
}

