  @State endMargin: number = 0 // 定义 Divider 的下边距，初始值为 0
  @State nullFlag: boolean = false // 定义一个标志位，用于控制 Divider 是否显示，初始值为 false

  build() {
    Column() {
      Tabs({ controller: this.controller1 }) { // 创建一个 Tabs 组件，并传入 TabsController 实例
        TabContent() { // 创建第一个标签页内容
          Column().width('100%').height('100%').backgroundColor(Color.Pink) // 创建一个 Column 组件，设置宽度和高度为 100%，背景颜色为粉色
        }.tabBar('pink') // 设置标签页的标签栏文本为 "pink"

        TabContent() { // 创建第二个标签页内容
          Column().width('100%').height('100%').backgroundColor(Color.Yellow) // 创建一个 Column 组件，设置宽度和高度为 100%，背景颜色为黄色
        }.tabBar('yellow') // 设置标签页的标签栏文本为 "yellow"

        TabContent() { // 创建第三个标签页内容
          Column().width('100%').height('100%').backgroundColor(Color.Blue) // 创建一个 Column 组件，设置宽度和高度为 100%，背景颜色为蓝色
        }.tabBar('blue') // 设置标签页的标签栏文本为 "blue"

        TabContent() { // 创建第四个标签页内容
          Column().width('100%').height('100%').backgroundColor(Color.Green) // 创建一个 Column 组件，设置宽度和高度为 100%，背景颜色为绿色
        }.tabBar('green') // 设置标签页的标签栏文本为 "green"

        TabContent() { // 创建第五个标签页内容
          Column().width('100%').height('100%').backgroundColor(Color.Red) // 创建一个 Column 组件，设置宽度和高度为 100%，背景颜色为红色
        }.tabBar('red') // 设置标签页的标签栏文本为 "red"
      }
      .vertical(true) // 设置 Tabs 组件为垂直方向
      .scrollable(true) // 设置 Tabs 组件可滚动
      .barMode(BarMode.Fixed) // 设置标签栏模式为固定模式
      .barWidth(70) // 设置标签栏的宽度为 70
      .barHeight(200) // 设置标签栏的高度为 200
      .animationDuration(400) // 设置标签页切换动画的持续时间为 400 毫秒
