@Entry
@Component
struct buttonTestDemo {
  @State txt: string = 'overflowTextOverlengthTextOverflow.Clip'; // 按钮的文本内容
  @State widthShortSize: number = 210; // 按钮的宽度

  build() {
    Row() {
      Column() {
        Button(this.txt) // 创建一个按钮，文本内容为txt
          .width(this.widthShortSize) // 设置按钮的宽度为widthShortSize
          .height(100) // 设置按钮的高度为100vp
          .backgroundColor(0x317aff) // 设置按钮的背景颜色为0x317aff
          .labelStyle({ 
            overflow: TextOverflow.Clip, // 设置文本溢出处理方式为裁剪
            maxLines: 1, // 设置文本最大行数为1
            minFontSize: 20, // 设置最小字体大小为20
            maxFontSize: 20, // 设置最大字体大小为20
            font: {
              size: 20, // 设置字体大小为20
              weight: FontWeight.Bolder, // 设置字体加粗
              family: 'cursive', // 设置字体家族为'cursive'
              style: FontStyle.Italic // 设置字体样式为斜体
            }
          })
          .fontSize(40) // 设置按钮文本的字体大小为40
      }
      .width('100%') // 设置Column的宽度为100%
    }
    .height('100%') // 设置Row的高度为100%
  }
}

@Entry
@Component
struct ButtonExample {
  build() {
    Flex({ direction: FlexDirection.Column, alignItems: ItemAlign.Start, justifyContent: FlexAlign.SpaceBetween }) {
      // 第一部分：普通按钮
      Text('Normal button').fontSize(9).fontColor(0xCCCCCC) // 标题文本，字体大小为9，颜色为灰色
      Flex({ alignItems: ItemAlign.Center, justifyContent: FlexAlign.SpaceBetween }) {
        Button('OK', { type: ButtonType.Normal, stateEffect: true }) // 普通按钮，显示文本为“OK”，启用状态效果
          .borderRadius(8) // 按钮边框圆角为8
          .backgroundColor(0x317aff) // 按钮背景颜色为蓝色
          .width(90) // 按钮宽度为90
          .onClick(() => {
            console.log('ButtonType.Normal') // 点击按钮时输出日志
          })
        Button({ type: ButtonType.Normal, stateEffect: true }) {
          Row() {
            LoadingProgress().width(20).height(20).margin({ left: 12 }).color(0xFFFFFF) // 加载进度条，宽度20，高度20，左边距12，颜色为白色
            Text('loading').fontSize(12).fontColor(0xffffff).margin({ left: 5, right: 12 }) // 文本“loading”，字体大小12，颜色为白色，左右边距分别为5和12
          }.alignItems(VerticalAlign.Center) // 行内元素垂直居中对齐
        }.borderRadius(8).backgroundColor(0x317aff).width(90).height(40) // 按钮边框圆角为8，背景颜色为蓝色，宽度90，高度40

        Button('Disable', { type: ButtonType.Normal, stateEffect: false }).opacity(0.4) // 禁用状态的普通按钮，显示文本为“Disable”，不启用状态效果，透明度为0.4
          .borderRadius(8).backgroundColor(0x317aff).width(90) // 按钮边框圆角为8，背景颜色为蓝色，宽度90
      }

      // 第二部分：胶囊按钮
      Text('Capsule button').fontSize(9).fontColor(0xCCCCCC) // 标题文本，字体大小为9，颜色为灰色
      Flex({ alignItems: ItemAlign.Center, justifyContent: FlexAlign.SpaceBetween }) {
        Button('OK', { type: ButtonType.Capsule, stateEffect: true }).backgroundColor(0x317aff).width(90) // 胶囊按钮，显示文本为“OK”，启用状态效果，背景颜色为蓝色，宽度90
        Button({ type: ButtonType.Capsule, stateEffect: true }) {
          Row() {
            LoadingProgress().width(20).height(20).margin({ left: 12 }).color(0xFFFFFF) // 加载进度条，宽度20，高度20，左边距12，颜色为白色
            Text('loading').fontSize(12).fontColor(0xffffff).margin({ left: 5, right: 12 }) // 文本“loading”，字体大小12，颜色为白色，左右边距分别为5和12
          }.alignItems(VerticalAlign.Center).width(90).height(40) // 行内元素垂直居中对齐，宽度90，高度40
        }.backgroundColor(0x317aff) // 按钮背景颜色为蓝色

        Button('Disable', { type: ButtonType.Capsule, stateEffect: false }).opacity(0.4) // 禁用状态的胶囊按钮，显示文本为“Disable”，不启用状态效果，透明度为0.4
          .backgroundColor(0x317aff).width(90) // 按钮背景颜色为蓝色，宽度90
      }

      // 第三部分：圆形按钮
      Text('Circle button').fontSize(9).fontColor(0xCCCCCC) // 标题文本，字体大小为9，颜色为灰色
      Flex({ alignItems: ItemAlign.Center, wrap: FlexWrap.Wrap }) {
        Button({ type: ButtonType.Circle, stateEffect: true }) {
          LoadingProgress().width(20).height(20).color(0xFFFFFF) // 加载进度条，宽度20，高度20，颜色为白色
        }.width(55).height(55).backgroundColor(0x317aff) // 圆形按钮，宽度55，高度55，背景颜色为蓝色

        Button({ type: ButtonType.Circle, stateEffect: true }) {
          LoadingProgress().width(20).height(20).color(0xFFFFFF) // 加载进度条，宽度20，高度20，颜色为白色
        }.width(55).height(55).margin({ left: 20 }).backgroundColor(0xF55A42) // 圆形按钮，宽度55，高度55，左边距20，背景颜色为红色
      }
    }.height(400).padding({ left: 35, right: 35, top: 35 }) // 整体布局高度为400，内边距为左右35，顶部35
  }
}

Image.code:
@Entry
@Component
struct ImageExample3 {
  // 定义三张图片资源
  private imageOne: Resource = $r('app.media.earth');
  private imageTwo: Resource = $r('app.media.star');
  private imageThree: Resource = $r('app.media.moveStar');

  // 定义两个状态变量，用于控制图片的显示
  @State src: Resource = this.imageOne;
  @State src2: Resource = this.imageThree;

  build() {
    Column() {
      // 第一张图片，点击后切换为第二张图片
      Image(this.src)
        .width(100) // 设置图片宽度为 100vp
        .height(100) // 设置图片高度为 100vp
        .onClick(() => {
          // 点击事件，切换图片为第二张图片
          this.src = this.imageTwo;
        })

      // 第二张图片，动画结束后切换为第一张图片
      Image(this.src2)
        .width(100) // 设置图片宽度为 100vp
        .height(100) // 设置图片高度为 100vp
        .onFinish(() => {
          // 动画结束事件，切换图片为第一张图片
          this.src2 = this.imageOne;
        })
    }
    .width('100%') // 设置布局宽度为 100%
    .height('100%') // 设置布局高度为 100%
  }
}

