Divider.code:
// xxx.ets
@Entry
@Component
struct DividerExample {
  build() {
    Column() {
      // 使用横向分割线场景
      Text('Horizontal divider').fontSize(9).fontColor(0xCCCCCC) // 标题文本，字体大小为9，颜色为#CCCCCC
      List() {
        ForEach([1, 2, 3], (item: number) => {
          ListItem() {
            Text('list' + item).width('100%').fontSize(14).fontColor('#182431').textAlign(TextAlign.Start) // 列表项文本，宽度为100%，字体大小为14，颜色为#182431，文本左对齐
          }.width(244).height(48) // 列表项宽度为244vp，高度为48vp
        }, (item: number) => item.toString())
      }.padding({ left: 24, bottom: 8 }) // 列表左内边距为24vp，底部内边距为8vp

      Divider().strokeWidth(8).color('#F1F3F5') // 水平分割线，宽度为8vp，颜色为#F1F3F5
      List() {
        ForEach([4, 5], (item: number) => {
          ListItem() {
            Text('list' + item).width('100%').fontSize(14).fontColor('#182431').textAlign(TextAlign.Start) // 列表项文本，宽度为100%，字体大小为14，颜色为#182431，文本左对齐
          }.width(244).height(48) // 列表项宽度为244vp，高度为48vp
        }, (item: number) => item.toString())
      }.padding({ left: 24, top: 8 }) // 列表左内边距为24vp，顶部内边距为8vp

      // 使用纵向分割线场景
      Text('Vertical divider').fontSize(9).fontColor(0xCCCCCC) // 标题文本，字体大小为9，颜色为#CCCCCC
      Column() {
        Column() {
          Row().width(288).height(64).backgroundColor('#30C9F0').opacity(0.3) // 背景行，宽度为288vp，高度为64vp，背景颜色为#30C9F0，透明度为0.3
          Row() {
            Button('Button')
              .width(136)
              .height(22)
              .fontSize(16)
              .fontColor('#007DFF')
              .fontWeight(500)
              .backgroundColor(Color.Transparent) // 按钮，宽度为136vp，高度为22vp，字体大小为16，颜色为#007DFF，字体粗细为500，背景透明
            Divider().vertical(true).height(22).color('#182431').opacity(0.6).margin({ left: 8, right: 8 }) // 垂直分割线，高度为22vp，颜色为#182431，透明度为0.6，左右边距为8vp
            Button('Button')
              .width(136)
              .height(22)
              .fontSize(16)
              .fontColor('#007DFF')
              .fontWeight(500)
              .backgroundColor(Color.Transparent) // 按钮，宽度为136vp，高度为22vp，字体大小为16，颜色为#007DFF，字体粗细为500，背景透明
          }.margin({ top: 17 }) // 按钮行，顶部外边距为17vp
        }
        .width(336)
        .height(152)
        .backgroundColor('#FFFFFF')
        .borderRadius(24)
        .padding(24) // 按钮列，宽度为336vp，高度为152vp，背景颜色为#FFFFFF，边框圆角为24vp，内边距为24vp
      }
      .width('100%')
      .height(168)
      .backgroundColor('#F1F3F5')
      .justifyContent(FlexAlign.Center)
      .margin({ top: 8 }) // 按钮列容器，宽度为100%，高度为168vp，背景颜色为#F1F3F5，内容居中对齐，顶部外边距为8vp
    }.width('100%').padding({ top: 24 }) // 主列，宽度为100%，顶部内边距为24vp
  }
}

