result.harmony_component_code:
Column() {
  RelativeContainer() {
    Column() {
      Text('Wind: 0 m/s')
        .id('wind')
        .fontSize(12)
        .textAlign(TextAlign.Start)
        .fontColor(Color.Black),
      Text('Pressure: 0 hpa')
        .id('pressure')
        .fontSize(12)
        .textAlign(TextAlign.Start)
        .fontColor(Color.Black),
      Text('Humidity: 0%')
        .id('humidity')
        .fontSize(12)
        .textAlign(TextAlign.Start)
        .fontColor(Color.Black)
    }
    .id('more_info')
    .alignRules({
      center: { anchor: '__container__', align: VerticalAlign.Center },
      left: { anchor: '__container__', align: HorizontalAlign.Start }
    }),
    Image($r('app.media.ic_cloud_white_24dp'))
      .id('icon')
      .width('auto')
      .height(64)
      .objectFit(ImageFit.FitCenter)
      .alignRules({
        center: { anchor: '__container__', align: VerticalAlign.Center },
        left: { anchor: 'more_info', align: HorizontalAlign.End }
      }),
    Column() {
      Text('0°C')
        .id('temperature')
        .fontSize(24)
        .textAlign(TextAlign.End)
        .fontColor(Color.Black),
      Text('Clear sky')
        .id('description')
        .fontSize(14)
        .textAlign(TextAlign.End)
        .fontColor(Color.Black)
    }
    .id('base_info')
    .alignRules({
      center: { anchor: '__container__', align: VerticalAlign.Center },
      right: { anchor: '__container__', align: HorizontalAlign.End }
    })
  }
  .width('100%')
  .height('100%')
  .padding(8)
}
