// 自定义按钮样式类，实现 ContentModifier 接口
class MyButtonStyle implements ContentModifier<ButtonConfiguration> {
  x: number = 0 // 点击位置的 x 坐标
  y: number = 0 // 点击位置的 y 坐标
  selectedColor: Color = Color.Black // 按压状态下的颜色

  // 构造函数，初始化 x、y 坐标和按压状态下的颜色
  constructor(x: number, y: number, ColorType: Color) {
    this.x = x
    this.y = y
    this.selectedColor = ColorType
  }

  // 应用内容方法，返回 WrappedBuilder
  applyContent(): WrappedBuilder<[ButtonConfiguration]> {
    return wrapBuilder(buildButton1) // 返回按钮构建器
  }
}

// 按钮构建器函数，根据配置构建按钮内容
@Builder function buildButton1(config: ButtonConfiguration) {
  Column({ space: 30 }) { // 垂直布局，组件间距为 30vp
    Text(config.enabled ? "enabled true" : "enabled false") // 显示按钮启用状态
    Text('圆圈状态' + (config.pressed ? "（ 按压 ）" : "（ 非按压 ）")) // 显示按钮按压状态
    Text('点击位置x坐标：' + (config.enabled ? (config.contentModifier as MyButtonStyle).x : "0")) // 显示点击位置的 x 坐标
    Text('点击位置y坐标：' + (config.enabled ? (config.contentModifier as MyButtonStyle).y : "0")) // 显示点击位置的 y 坐标
    Circle({ width: 50, height: 50 }) // 圆形组件，宽度和高度为 50vp
      .fill(config.pressed ? (config.contentModifier as MyButtonStyle).selectedColor : Color.Black) // 根据按压状态设置填充颜色
      .gesture(
        TapGesture({ count: 1 }).onAction((event: GestureEvent) => { // 单击手势，触发点击事件
          config.triggerClick(event.fingerList[0].localX, event.fingerList[0].localY) // 触发点击事件，传递点击位置
        })).opacity(config.enabled ? 1 : 0.1) // 根据启用状态设置透明度
  }
}

@Entry
@Component
struct ButtonExample {
  @State buttonEnabled: boolean = true; // 按钮启用状态
  @State positionX: number = 0 // 点击位置的 x 坐标
  @State positionY: number = 0 // 点击位置的 y 坐标
  @State state: boolean[] = [true, false] // 按钮状态数组
  @State index: number = 0 // 按钮状态索引

  build() {
    Column() { // 垂直布局
      Button('OK') // 按钮组件，显示文本 "OK"
        .contentModifier(new MyButtonStyle(this.positionX, this.positionY, Color.Red)) // 设置按钮样式
        .onClick((event) => { // 点击事件处理函数
          console.info('change' + JSON.stringify(event)) // 输出点击事件信息
          this.positionX = event.displayX // 更新点击位置的 x 坐标
          this.positionY = event.displayY // 更新点击位置的 y 坐标
        }).enabled(this.buttonEnabled) // 设置按钮启用状态
      Row() { // 水平布局
        Toggle({ type: ToggleType.Switch, isOn: true }) // 开关组件，初始状态为开启
          .onChange((value: boolean) => { // 开关状态变化事件处理函数
            if (value) {
              this.buttonEnabled = true // 启用按钮
            } else {
              this.buttonEnabled = false // 禁用按钮
            }
          }).margin({ left: -80 }) // 设置开关组件的左边距
      }
    }.height('100%').width('100%').justifyContent(FlexAlign.Center) // 设置布局的高度和宽度为 100%，并居中对齐
  }
}

@Entry
@Component
struct ButtonExample {
  build() {
    Flex({ direction: FlexDirection.Column, alignItems: ItemAlign.Start, justifyContent: FlexAlign.SpaceBetween }) {
      // 显示标题“Role is Normal button”，字体大小为9，颜色为0xCCCCCC
      Text('Role is Normal button').fontSize(9).fontColor(0xCCCCCC)
      // 使用Flex布局，子元素居中对齐，子元素之间有空间间隔
      Flex({ alignItems: ItemAlign.Center, justifyContent: FlexAlign.SpaceBetween }) {
        // 创建一个强调样式且角色为正常的按钮，按钮文本为“Emphasized”
        Button('Emphasized', { buttonStyle: ButtonStyleMode.EMPHASIZED, role: ButtonRole.NORMAL });
        // 创建一个普通样式且角色为正常的按钮，按钮文本为“Normal”
        Button('Normal', { buttonStyle: ButtonStyleMode.NORMAL, role: ButtonRole.NORMAL });
        // 创建一个文本样式且角色为正常的按钮，按钮文本为“Textual”
        Button('Textual', { buttonStyle: ButtonStyleMode.TEXTUAL, role: ButtonRole.NORMAL });
      }
      // 显示标题“Role is Error button”，字体大小为9，颜色为0xCCCCCC
      Text('Role is Error button').fontSize(9).fontColor(0xCCCCCC)
      // 使用Flex布局，子元素居中对齐，子元素之间有空间间隔
      Flex({ alignItems: ItemAlign.Center, justifyContent: FlexAlign.SpaceBetween }) {
        // 创建一个强调样式且角色为错误的按钮，按钮文本为“Emphasized”
        Button('Emphasized', { buttonStyle: ButtonStyleMode.EMPHASIZED, role: ButtonRole.ERROR});
        // 创建一个普通样式且角色为错误的按钮，按钮文本为“Normal”
        Button('Normal', { buttonStyle: ButtonStyleMode.NORMAL, role: ButtonRole.ERROR });
        // 创建一个文本样式且角色为错误的按钮，按钮文本为“Textual”
        Button('Textual', { buttonStyle: ButtonStyleMode.TEXTUAL, role: ButtonRole.ERROR });
      }
    }.height(200).padding({ left: 35, right: 35, top: 35 })
  }
}

