@Entry
@Component
struct ButtonExample {
  build() {
    Flex({ direction: FlexDirection.Column, alignItems: ItemAlign.Start, justifyContent: FlexAlign.SpaceBetween }) {
      // 显示文本“Normal button”
      Text('Normal button').fontSize(9).fontColor(0xCCCCCC)
      // 创建一个Flex布局，对齐方式为中心对齐，间距均匀分布
      Flex({ alignItems: ItemAlign.Center, justifyContent: FlexAlign.SpaceBetween }) {
        // 创建一个显示文本为“OK”的普通按钮，启用状态效果
        Button('OK', { type: ButtonType.Normal, stateEffect: True })
          .borderRadius(8) // 设置按钮的边框圆角半径为8
          .backgroundColor(0x317aff) // 设置按钮的背景颜色为蓝色
          .width(90) // 设置按钮的宽度为90
          .onClick(() => {
            console.log('ButtonType.Normal') // 点击按钮时在控制台输出信息
          })
        // 创建一个显示加载中的普通按钮，启用状态效果
        Button({ type: ButtonType.Normal, stateEffect: True }) {
          Row() {
            LoadingProgress().width(20).height(20).margin({ left: 12 }).color(0xFFFFFF) // 加载进度条
            Text('loading').fontSize(12).fontColor(0xffffff).margin({ left: 5, right: 12 }) // 加载文本
          }.alignItems(VerticalAlign.Center) // 垂直居中对齐
        }.borderRadius(8).backgroundColor(0x317aff).width(90).height(40) // 设置按钮样式

        // 创建一个显示文本为“Disable”的普通按钮，禁用状态效果，设置透明度为0.4
        Button('Disable', { type: ButtonType.Normal, stateEffect: False }).opacity(0.4)
          .borderRadius(8).backgroundColor(0x317aff).width(90)
      }

      // 显示文本“Capsule button”
      Text('Capsule button').fontSize(9).fontColor(0xCCCCCC)
      // 创建一个Flex布局，对齐方式为中心对齐，间距均匀分布
      Flex({ alignItems: ItemAlign.Center, justifyContent: FlexAlign.SpaceBetween }) {
        // 创建一个显示文本为“OK”的胶囊按钮，启用状态效果
        Button('OK', { type: ButtonType.Capsule, stateEffect: True }).backgroundColor(0x317aff).width(90)
        // 创建一个显示加载中的胶囊按钮，启用状态效果
        Button({ type: ButtonType.Capsule, stateEffect: True }) {
          Row() {
            LoadingProgress().width(20).height(20).margin({ left: 12 }).color(0xFFFFFF) // 加载进度条
            Text('loading').fontSize(12).fontColor(0xffffff).margin({ left: 5, right: 12 }) // 加载文本
          }.alignItems(VerticalAlign.Center).width(90).height(40) // 垂直居中对齐
        }.backgroundColor(0x317aff)

        // 创建一个显示文本为“Disable”的胶囊按钮，禁用状态效果，设置透明度为0.4
        Button('Disable', { type: ButtonType.Capsule, stateEffect: False }).opacity(0.4)
          .backgroundColor(0x317aff).width(90)
      }

      // 显示文本“Circle button”
      Text('Circle button').fontSize(9).fontColor(0xCCCCCC)
      // 创建一个Flex布局，对齐方式为中心对齐，允许换行
      Flex({ alignItems: ItemAlign.Center, wrap: FlexWrap.Wrap }) {
        // 创建一个显示加载中的圆形按钮，启用状态效果
        Button({ type: ButtonType.Circle, stateEffect: True }) {
          LoadingProgress().width(20).height(20).color(0xFFFFFF) // 加载进度条
        }.width(55).height(55).backgroundColor(0x317aff) // 设置按钮样式

        // 创建另一个显示加载中的圆形按钮，启用状态效果，设置不同的背景颜色
        Button({ type: ButtonType.Circle, stateEffect: True }) {
          LoadingProgress().width(20).height(20).color(0xFFFFFF) // 加载进度条
        }.width(55).height(55).margin({ left: 20 }).backgroundColor(0xF55A42) // 设置按钮样式
      }
    }.height(400).padding({ left: 35, right: 35, top: 35 }) // 设置整体布局的高度和内边距
  }
}

@Entry
@Component
struct CalendarPickerExample {
  // 定义一个私有变量selectedDate，用于存储当前选中的日期，初始值为2024年3月5日
  private selectedDate: Date = new Date('2024-03-05')

  build() {
    Column() {
      // 创建一个文本组件，显示标题“月历日期选择器”，字体大小为30
      Text('月历日期选择器').fontSize(30)
      Column() {
        // 创建一个CalendarPicker组件，设置提示圆点半径为10，初始选中日期为selectedDate
        CalendarPicker({ hintRadius: 10, selected: this.selectedDate })
          .edgeAlign(CalendarAlign.END) // 设置月历选择器的对齐方式为右侧对齐
          .textStyle({ color: "  # ff182431", font: { size: 20, weight: FontWeight.Normal } }) // 设置月历选择器的文本样式，包括颜色和字体大小
          .margin(10) // 设置月历选择器的边距为10
          .onChange((value) => {
            // 当用户选择日期时，触发onChange事件，并在控制台输出选中的日期信息
            console.info("CalendarPicker onChange:" + JSON.stringify(value))
          })
      }.alignItems(HorizontalAlign.End).width("100%") // 设置内部Column组件的子元素水平对齐方式为右侧对齐，宽度为100%
    }.width('100%').margin({ top: 350 }) // 设置外部Column组件的宽度为100%，顶部边距为350
  }
}

@Entry
@Component
struct CheckedTextView {
  // 定义一个状态变量checked，用于存储Checkbox的选中状态，初始值为false
  @State checked: boolean = false; 

  build() {
    // 使用Flex布局，设置布局方向为行（水平），对齐方式为中心对齐
    Flex({ direction: FlexDirection.Row, alignItems: ItemAlign.Center, justifyContent: FlexAlign.Center }) {
      // 创建一个Checkbox组件，绑定到状态变量checked，设置宽度和高度
      Checkbox()
        .select($$this.checked) // 绑定Checkbox的选中状态到状态变量checked
        .width(30) // 设置Checkbox的宽度为30
        .height(30) // 设置Checkbox的高度为30

      // 创建一个Text组件，显示文本"Click to check"，设置字体大小和文本对齐方式
      Text('Click to check')
        .fontSize(18) // 设置字体大小为18
        .textAlign(TextAlign.Center) // 设置文本对齐方式为中心对齐
        .onClick(() => {
          // 点击Text时，切换状态变量checked的值
          this.checked = !this.checked
        })
