@Entry
@Component
struct Index {
  scene: SceneOptions = { scene: $rawfile('gltf/DamageHemlt/glTF/DamagedHelmet.gltf'), modelType: ModelType.SURFACE}; // 定义场景选项，包括加载的3D模型和模型类型
  backAnimator: AnimatorResult = animator.create({
    duration: 2000, // 动画持续时间，单位为毫秒
    easing: "ease", // 缓动函数，控制动画的加速度
    delay: 0, // 延迟时间，单位为毫秒
    fill: "none", // 动画填充模式，none表示动画结束后不保留状态
    direction: "normal", // 播放方向，normal表示正向播放
    iterations: -1, // 播放次数，-1为无限循环
    begin: 100, // 起始值
    end: 200, // 结束值
  });
  @State timeDelta: number[] = [1.0, 2.0]; // 时间间隔数组，用于传递给着色器

  // 创建函数，设置动画回调
  create() {
    this.backAnimator.onfinish = () => {
      console.log('backAnimator onfinish'); // 动画播放完成回调
    }
    this.backAnimator.onframe = value => {
      TickFrame(); // 模拟每帧更新
      this.timeDelta[0] = engineTime.deltaTimeUs; // 更新时间间隔数组
    }
  }

  // 构建函数，渲染UI组件
  build() {
    Row() {
      Column() {
        Text('custom rendering') // 显示文本
        Component3D()
          .shader($rawfile('assets/app/shaders/shader/London.shader')) // 设置着色器
          .shaderImageTexture($rawfile('assets/London.jpg')) // 设置着色器纹理
          .shaderInputBuffer(this.timeDelta) // 设置着色器输入缓冲
          .customRender($rawfile('assets/app/rendernodegraphs/London.rng'), true) // 自定义渲染
          .renderWidth('90%').renderHeight('90%') // 设置渲染宽度和高度
          .onAppear(() => {
            this.create(); // 创建动画回调
            this.backAnimator.play(); // 播放动画
          }).width('50%').height('50%') // 设置宽度和高度
      }.width('100%') // 设置宽度
    }
    .height('100%') // 设置高度
  }
}

