@Entry
@Component
struct ImageAnimatorExample {
  @State state: AnimationStatus = AnimationStatus.Initial // 动画状态，默认为初始状态
  @State reverse: boolean = false // 是否反向播放动画
  @State iterations: number = 1 // 动画循环次数，默认为1次

  build() {
    Column({ space: 10 }) {
      // 创建图片动画组件，设置多张图片帧和动画属性
      ImageAnimator()
        .images([
          { src: $r('app.media.img1') }, // 第一帧图片
          { src: $r('app.media.img2') }, // 第二帧图片
          { src: $r('app.media.img3') }, // 第三帧图片
          { src: $r('app.media.img4') }  // 第四帧图片
        ])
        .duration(2000) // 设置动画持续时间为2秒
        .state(this.state) // 设置动画状态
        .reverse(this.reverse) // 设置是否反向播放
        .fillMode(FillMode.None) // 设置填充模式为None
        .iterations(this.iterations) // 设置动画循环次数
        .width(340) // 设置动画组件宽度
        .height(240) // 设置动画组件高度
        .margin({ top: 100 }) // 设置顶部边距
        .onStart(() => {
          console.info('Start') // 动画开始时的回调
        })
        .onPause(() => {
          console.info('Pause') // 动画暂停时的回调
        })
        .onRepeat(() => {
          console.info('Repeat') // 动画重复播放时的回调
        })
        .onCancel(() => {
          console.info('Cancel') // 动画取消时的回调
        })
        .onFinish(() => {
          console.info('Finish') // 动画结束时的回调
          this.state = AnimationStatus.Stopped // 将动画状态设置为停止
        })

      // 创建按钮行，用于控制动画的开始、暂停和停止
      Row() {
        Button('start').width(100).padding(5).onClick(() => {
          this.state = AnimationStatus.Running // 开始播放动画
        }).margin(5)
        Button('pause').width(100).padding(5).onClick(() => {
          this.state = AnimationStatus.Paused // 暂停动画
        }).margin(5)
        Button('stop').width(100).padding(5).onClick(() => {
          this.state = AnimationStatus.Stopped // 停止动画
        }).margin(5)
      }

      // 创建按钮行，用于控制动画的反向播放、单次循环和无限循环
      Row() {
        Button('reverse').width(100).padding(5).onClick(() => {
          this.reverse = !this.reverse // 切换反向播放状态
        }).margin(5)
        Button('once').width(100).padding(5).onClick(() => {
          this.iterations = 1 // 设置为单次循环
        }).margin(5)
        Button('infinite').width(100).padding(5).onClick(() => {
          this.iterations = -1 // 设置为无限循环播放
        }).margin(5)
      }
    }.width('100%').height('100%') // 设置容器宽度和高度
  }
}

