@Entry
@Component
struct MarqueeExample {
  @State start: boolean = false // 控制跑马灯动画开始或停止的状态
  @State src: string = '' // 跑马灯文本内容的来源
  @State marqueeText: string = 'Running Marquee' // 默认的跑马灯文本内容

  private fromStart: boolean = true // 跑马灯动画是否从头开始的标识
  private step: number = 10 // 跑马灯文本滚动的步长
  private loop: number = Number.POSITIVE_INFINITY // 跑马灯文本滚动的循环次数

  controller: TextClockController = new TextClockController() // 文本时钟控制器实例

  // 将数字转换为时间格式的字符串，格式为"HH:MM:SS"
  convert2time(value: number): string {
    let date = new Date(Number(value + '000'));
    let hours = date.getHours().toString().padStart(2, '0');
    let minutes = date.getMinutes().toString().padStart(2, '0');
    let seconds = date.getSeconds().toString().padStart(2, '0');
    return hours + ":" + minutes + ":" + seconds;
  }

  // 构建跑马灯示例组件
  build() {
    Flex({ direction: FlexDirection.Column, alignItems: ItemAlign.Center, justifyContent: FlexAlign.Center }) {
      // 创建跑马灯组件，并设置相关属性
      Marquee({
        start: this.start, // 控制跑马灯动画是否开始
        step: this.step, // 跑马灯文本滚动的步长
        loop: this.loop, // 跑马灯文本滚动的循环次数
        fromStart: this.fromStart, // 跑马灯动画是否从头开始
        src: this.marqueeText + this.src // 跑马灯显示的文本内容
      })
        .marqueeUpdateStrategy(MarqueeUpdateStrategy.PRESERVE_POSITION) // 设置跑马灯更新策略，保持当前位置
        .width(300) // 设置跑马灯组件的宽度
        .height(80) // 设置跑马灯组件的高度
        .fontColor('#FFFFFF') // 设置跑马灯文本的颜色
        .fontSize(48) // 设置跑马灯文本的字体大小
        .fontWeight(700) // 设置跑马灯文本的字体粗细
        .backgroundColor('#182431') // 设置跑马灯组件的背景颜色
        .margin({ bottom: 40 }) // 设置跑马灯组件的下边距
        .onStart(() => {
          console.info('Marquee animation complete onStart') // 跑马灯动画开始时的回调
        })
        .onBounce(() => {
          console.info('Marquee animation complete onBounce') // 跑马灯动画反弹时的回调
        })
        .onFinish(() => {
          console.info('Marquee animation complete onFinish') // 跑马灯动画完成时的回调
        })

      // 创建一个按钮用于启动跑马灯动画
      Button('Start')
        .onClick(() => {
          this.start = true // 启动跑马灯动画
          // 启动文本时钟
          this.controller.start()
        })
        .width(120) // 设置按钮的宽度
        .height(40) // 设置按钮的高度
        .fontSize(16) // 设置按钮文本的字体大小
        .fontWeight(500) // 设置按钮文本的字体粗细
        .backgroundColor('#007DFF') // 设置按钮的背景颜色

      // 创建文本时钟组件，用于显示时间
      TextClock({ timeZoneOffset: -8, controller: this.controller }) // 设置时区偏移量和控制器
        .format('hms') // 设置时间格式为"HH:MM:SS"
        .onDateChange((value: number) => {
          this.src = this.convert2time(value); // 当时间变化时，更新跑马灯文本内容
        })
        .margin(20) // 设置文本时钟组件的外边距
        .fontSize(30) // 设置文本时钟文本的字体大小
    }
    .width('100%') // 设置Flex容器的宽度为100%
    .height('100%') // 设置Flex容器的高度为100%
  }
}

