import { LengthUnit } from '@kit.ArkUI'

@Entry
@Component
struct PatternLockExample {
  @State passwords: Number[] = [] // 用于存储用户输入的密码
  @State message: string = 'please input password!' // 初始提示信息，提示用户输入密码
  private patternLockController: PatternLockController = new PatternLockController() // 创建图案锁控制器实例，用于控制图案锁的行为

  build() {
    Column() {
      Text(this.message) // 显示提示信息
        .textAlign(TextAlign.Center) // 文本居中对齐
        .margin(20) // 设置文本外边距
        .fontSize(20) // 设置文本字体大小

      PatternLock(this.patternLockController) // 创建图案锁组件，并传入控制器实例
        .sideLength(200) // 设置图案锁的边长
        .circleRadius(9) // 设置图案锁中圆点的半径
        .pathStrokeWidth(18) // 设置图案锁路径线的宽度
        .activeColor('#B0C4DE') // 设置图案锁激活状态的颜色
        .selectedColor('#228B22') // 设置图案锁选中状态的颜色
        .pathColor('#90EE90') // 设置图案锁路径线的颜色
        .backgroundColor('#F5F5F5') // 设置图案锁的背景颜色
        .autoReset(true) // 设置图案锁在完成一次输入后是否自动重置
        .activateCircleStyle({
          color: '#90EE90', // 设置激活圆点的颜色
          radius: { value: 16, unit: LengthUnit.VP }, // 设置激活圆点的半径，单位为视口宽度单位
          enableWaveEffect: true // 启用激活圆点的波浪效果
        })
        .onDotConnect((index: number) => {
          console.log("onDotConnect index: " + index) // 当用户连接圆点时触发，打印连接的圆点索引
        })
        .onPatternComplete((input: Array<number>) => {
          // 判断用户输入的密码长度是否符合要求
          if (input === null || input === undefined || input.length < 5) {
            this.message = 'The password length needs to be greater than 5, please enter again.' // 提示用户密码长度不足，需要重新输入
            return
          }
          // 判断是否已经设置过密码
          if (this.passwords.length > 0) {
            // 判断两次输入的密码是否一致
            if (this.passwords.toString() === input.toString()) {
              this.passwords = input // 更新密码
              this.message = 'Set password successfully: ' + this.passwords.toString() // 提示用户密码设置成功
              this.patternLockController.setChallengeResult(PatternLockChallengeResult.CORRECT) // 设置图案锁挑战结果为正确
            } else {
              this.message = 'Inconsistent passwords, please enter again.' // 提示用户两次输入的密码不一致，需要重新输入
              this.patternLockController.setChallengeResult(PatternLockChallengeResult.WRONG) // 设置图案锁挑战结果为错误
            }
          } else {
            this.passwords = input // 第一次输入密码，存储密码
            this.message = "Please enter again." // 提示用户再次输入密码
          }
        })

      Button('Reset PatternLock') // 创建重置按钮
        .margin(30) // 设置按钮外边距
        .onClick(() => {
          // 重置图案锁
          this.patternLockController.reset() // 重置图案锁状态
          this.passwords = [] // 清空存储的密码
          this.message = 'Please input password' // 提示用户重新输入密码
        })
    }
    .width('100%') // 设置布局宽度为100%
    .height('100%') // 设置布局高度为100%
  }
}

