struct SpanExample {
  build() {
    // 创建一个垂直方向的 Flex 布局，子元素垂直从头部对齐，间距均匀分布
    Flex({ direction: FlexDirection.Column, alignItems: ItemAlign.Start, justifyContent: FlexAlign.SpaceBetween }) {

      // 基本用法示例
      Text('Basic Usage').fontSize(9).fontColor(0xCCCCCC) // 显示标题 "Basic Usage"，设置字体大小和颜色
      Text() {
        Span('In Line') // 创建一个 Span 组件，显示文本 "In Line"
        Span(' Component') // 创建一个 Span 组件，显示文本 " Component"
        Span(' !') // 创建一个 Span 组件，显示文本 " !"
      }

      // Span 组件示例，设置文本大小写为正常，去除装饰线，字体颜色为红色
      Text() {
        Span('This is the Span component').fontSize(12).textCase(TextCase.Normal) // 创建一个 Span 组件，显示文本 "This is the Span component"，设置字体大小和文本大小写为正常
          .decoration({ type: TextDecorationType.None, color: Color.Red }) // 设置文本装饰线为无，颜色为红色
      }

      // 文本添加下划线，样式为波浪线
      Text('Text Decoration').fontSize(9).fontColor(0xCCCCCC) // 显示标题 "Text Decoration"，设置字体大小和颜色
      Text() {
        Span('I am Underline-WAVY-span').decoration({ type: TextDecorationType.Underline, color: Color.Red, style: TextDecorationStyle.WAVY }).fontSize(12) // 创建一个 Span 组件，显示文本 "I am Underline-WAVY-span"，设置下划线装饰线，颜色为红色，样式为波浪线，字体大小为12
      }

      // 文本添加删除线，样式为点状
      Text() {
        Span('I am LineThrough-DOTTED-span') // 创建一个 Span 组件，显示文本 "I am LineThrough-DOTTED-span"
          .decoration({ type: TextDecorationType.LineThrough, color: Color.Red, style: TextDecorationStyle.DOTTED }) // 设置删除线装饰线，颜色为红色，样式为点状
          .fontSize(12) // 设置字体大小为12
      }

      // 文本添加上划线，样式为虚线
      Text() {
        Span('I am Overline-DASHED-span').decoration({ type: TextDecorationType.Overline, color: Color.Red, style: TextDecorationStyle.DASHED }).fontSize(12) // 创建一个 Span 组件，显示文本 "I am Overline-DASHED-span"，设置上划线装饰线，颜色为红色，样式为虚线，字体大小为12
      }

      // 文本字符间距示例
      Text('LetterSpacing').fontSize(9).fontColor(0xCCCCCC) // 显示标题 "LetterSpacing"，设置字体大小和颜色
      Text() {
        Span('span letter spacing') // 创建一个 Span 组件，显示文本 "span letter spacing"
          .letterSpacing(0) // 设置字符间距为0
          .fontSize(12) // 设置字体大小为12
      }

      Text() {
        Span('span letter spacing') // 创建一个 Span 组件，显示文本 "span letter spacing"
          .letterSpacing(-2) // 设置字符间距为-2
          .fontSize(12) // 设置字体大小为12
      }

      Text() {
        Span('span letter spacing') // 创建一个 Span 组件，显示文本 "span letter spacing"
          .letterSpacing(3) // 设置字符间距为3
          .fontSize(12) // 设置字体大小为12
      }

      // 文本大小写展示设置示例
      Text('Text Case').fontSize(9).fontColor(0xCCCCCC) // 显示标题 "Text Case"，设置字体大小和颜色
      Text() {
        Span('I am Lower-span').fontSize(12) // 创建一个 Span 组件，显示文本 "I am Lower-span"，设置字体大小为12
          .textCase(TextCase.LowerCase) // 设置文本大小写为全小写
          .decoration({ type: TextDecorationType.None }) // 设置文本装饰线为无
      }

      Text() {
        Span('I am Upper-span').fontSize(12) // 创建一个 Span 组件，显示文本 "I am Upper-span"，设置字体大小为12
          .textCase(TextCase.UpperCase) // 设置文本大小写为全大写
          .decoration({ type: TextDecorationType.None }) // 设置文本装饰线为无
      }
    }.width('100%').height(250).padding({ left: 35, right: 35, top: 35 }) // 设置 Flex 布局的宽度为100%，高度为250，内边距为35
  }
}

