@Component
struct StepperExample {
  @State currentIndex: number = 0; // 当前步骤索引，用于控制显示哪个步骤页
  @State firstState: ItemState = ItemState.Normal; // 第一个步骤的状态，初始为正常状态
  @State secondState: ItemState = ItemState.Normal; // 第二个步骤的状态，初始为正常状态
  @State thirdState: ItemState = ItemState.Normal; // 第三个步骤的状态，初始为正常状态

  build() {
    Stepper({
      index: this.currentIndex // 设置当前步骤索引
    }) {
      // 第一个步骤页
      StepperItem() {
        Column() {
          Text('Page One')
            .itemTextStyle(); // 应用itemTextStyle样式
          Button('change status:' + this.firstState)
            .backgroundColor('#007dFF') // 设置背景颜色为蓝色
            .onClick(() => {
              // 切换第一个步骤的状态，如果当前状态是跳过，则切换为正常，否则切换为跳过
              this.firstState = this.firstState === ItemState.Skip ? ItemState.Normal : ItemState.Skip;
            });
        }.itemStyle(); // 应用itemStyle样式
      }
      .nextLabel('Next') // 设置下一步按钮文本
      .status(this.firstState); // 设置步骤状态

      // 第二个步骤页
      StepperItem() {
        Column() {
          Text('Page Two')
            .itemTextStyle(); // 应用itemTextStyle样式
          Button('change status:' + this.secondState)
            .backgroundColor('#007dFF') // 设置背景颜色为蓝色
            .onClick(() => {
              // 切换第二个步骤的状态，如果当前状态是禁用，则切换为正常，否则切换为禁用
              this.secondState = this.secondState === ItemState.Disabled ? ItemState.Normal : ItemState.Disabled;
            });
        }.itemStyle(); // 应用itemStyle样式
      }
      .nextLabel('Next') // 设置下一步按钮文本
      .prevLabel('Previous') // 设置上一步按钮文本
      .status(this.secondState); // 设置步骤状态

      // 第三个步骤页
      StepperItem() {
        Column() {
          Text('Page Three')
            .itemTextStyle(); // 应用itemTextStyle样式
          Button('change status:' + this.thirdState)
            .backgroundColor('#007dFF') // 设置背景颜色为蓝色
            .onClick(() => {
              // 切换第三个步骤的状态，如果当前状态是等待，则切换为正常，否则切换为等待
              this.thirdState = this.thirdState === ItemState.Waiting ? ItemState.Normal : ItemState.Waiting;
            });
        }.itemStyle(); // 应用itemStyle样式
      }
      .status(this.thirdState); // 设置步骤状态

      // 第四个步骤页
      StepperItem() {
        Column() {
          Text('Page Four')
            .itemTextStyle(); // 应用itemTextStyle样式
        }.itemStyle(); // 应用itemStyle样式
      }
    }
    .backgroundColor('#F1F3F5') // 设置背景颜色为灰色
    .onFinish(() => {
      // 处理点击最后一页的Finish时的逻辑
      console.info('onFinish');
    })
    .onSkip(() => {
      // 处理点击跳过时的逻辑
      console.info('onSkip');
    })
    .onChange((prevIndex?: number, index?: number) => {
      if(index){
        this.currentIndex = index; // 更新当前步骤索引
      }
    });
  }
}

// 定义步骤页的样式
@Styles function itemStyle () {
  .width(336) // 设置宽度
  .height(621) // 设置高度
  .margin({ top: 48, left: 12 }) // 设置外边距
  .borderRadius(24) // 设置圆角
  .backgroundColor('#FFFFFF'); // 设置背景颜色为白色
}

