@Entry
@Component
struct TextExample11 {
  @State text: string = 'Text editMenuOptions' // 定义一个状态变量 `text`，用于存储文本内容

  // 定义一个方法 `onCreateMenu`，用于自定义编辑菜单项
  onCreateMenu(menuItems: Array<TextMenuItem>) {
    menuItems.forEach((value, index) => {
      value.icon = $r('app.media.startIcon') // 为每个菜单项设置图标
      if (value.id.equals(TextMenuItemId.COPY)) { // 如果菜单项的 ID 是 COPY
        value.content = "复制change" // 修改复制菜单项的文本内容
      }
      if (value.id.equals(TextMenuItemId.SELECT_ALL)) { // 如果菜单项的 ID 是 SELECT_ALL
        value.content = "全选change" // 修改全选菜单项的文本内容
      }
    })
    let item1: TextMenuItem = {
      content: 'custom1', // 自定义菜单项的文本内容
      icon: $r('app.media.startIcon'), // 自定义菜单项的图标
      id: TextMenuItemId.of('custom1'), // 自定义菜单项的 ID
    }
    let item2: TextMenuItem = {
      content: 'custom2', // 自定义菜单项的文本内容
      id: TextMenuItemId.of('custom2'), // 自定义菜单项的 ID
      icon: $r('app.media.startIcon'), // 自定义菜单项的图标
    }
    menuItems.push(item1) // 将自定义菜单项 `item1` 添加到菜单项列表的末尾
    menuItems.unshift(item2) // 将自定义菜单项 `item2` 添加到菜单项列表的开头
    return menuItems // 返回修改后的菜单项列表
  }

  build() {
    Column() {
      Text(this.text) // 显示文本内容
        .fontSize(20) // 设置文本字体大小
        .copyOption(CopyOptions.LocalDevice) // 设置复制选项为仅本地设备
        .editMenuOptions({
          onCreateMenu: this.onCreateMenu, // 设置自定义菜单项的回调方法
          onMenuItemClick: (menuItem: TextMenuItem, textRange: TextRange) => { // 设置菜单项点击的回调方法
            if (menuItem.id.equals(TextMenuItemId.of("custom2"))) { // 如果点击的是自定义菜单项 `custom2`
              console.log("拦截 id: custom2 start:" + textRange.start + "; end:" + textRange.end) // 输出日志，表示拦截了该菜单项的点击事件
              return true; // 返回 true，表示拦截该菜单项的默认行为
            }
            if (menuItem.id.equals(TextMenuItemId.COPY)) { // 如果点击的是复制菜单项
              console.log("拦截 COPY start:" + textRange.start + "; end:" + textRange.end) // 输出日志，表示拦截了该菜单项的点击事件
              return true; // 返回 true，表示拦截该菜单项的默认行为
            }
            if (menuItem.id.equals(TextMenuItemId.SELECT_ALL)) { // 如果点击的是全选菜单项
              console.log("不拦截 SELECT_ALL start:" + textRange.start + "; end:" + textRange.end) // 输出日志，表示不拦截该菜单项的点击事件
              return false; // 返回 false，表示不拦截该菜单项的默认行为
            }
            return false; // 返回 false，表示不拦截其他菜单项的默认行为
          }
        })
        .margin({ top: 100 }) // 设置文本组件的上边距
    }
    .width("90%") // 设置列组件的宽度为父容器的 90%
    .margin("5%") // 设置列组件的外边距为父容器的 5%
  }
}

