  options: TextOptions = { controller: this.controller };

  build() {
    Column() {
      Column() {
        // 创建一个Text组件，包含文本和图像，并配置控制器
        Text(undefined, this.options) {
          Span('Hello World') // 添加文本内容
          ImageSpan($r('app.media.icon')) // 添加图像内容
            .width('100px') // 设置图像宽度
            .height('100px') // 设置图像高度
            .objectFit(ImageFit.Fill) // 设置图像填充方式
            .verticalAlign(ImageSpanAlignment.CENTER) // 设置图像垂直对齐方式
        }
        .copyOption(CopyOptions.InApp) // 设置文本复制选项为仅在应用内复制
        .bindSelectionMenu(TextSpanType.IMAGE, this.LongPressImageCustomMenu, TextResponseType.LONG_PRESS, {
          // 绑定长按图像时的自定义菜单
          onDisappear: () => {
            console.info(`自定义选择菜单关闭时回调`); // 菜单关闭时的回调
