          .contentModifier(this.myTimerModifier) // 应用自定义修饰器
          .onTimer((utc: number, elapsedTime: number) => {
            // 计时器触发时的回调函数，输出当前时间戳和已过去的时间
            console.info('textTimer onTimer utc is：' + utc + ', elapsedTime: ' + elapsedTime)
          })
          .margin(10) // 设置组件的外边距
        // 创建一个正计时TextTimer组件，应用自定义修饰器，并绑定控制器
