@Component
struct TextInputExample {
  // 创建一个TextInputController实例，用于控制TextInput的行为
  controller: TextInputController = new TextInputController()

  // 定义一个状态变量inputValue，用于存储TextInput的输入值
  @State inputValue: string = ""

  // 自定义键盘的构建器，用于生成自定义键盘的UI
  @Builder CustomKeyboardBuilder() {
    Column() {
      // 创建一个按钮，点击后停止编辑TextInput
      Button('x').onClick(() => {
        this.controller.stopEditing()
      })

      // 创建一个Grid布局，用于放置键盘的按键
      Grid() {
        // 使用ForEach循环生成键盘的按键
        ForEach([1, 2, 3, 4, 5, 6, 7, 8, 9, '*', 0, '#'], (item: number | string) => {
          GridItem() {
            // 创建一个按钮，按钮的文本为当前循环的item
            Button(item + "")
              .width(110) // 设置按钮的宽度
              .onClick(() => {
                // 点击按钮时，将item添加到inputValue中
                this.inputValue += item
              })
          }
        })
      }
      .maxCount(3) // 设置Grid的最大列数为3
      .columnsGap(10) // 设置列之间的间距
      .rowsGap(10) // 设置行之间的间距
      .padding(5) // 设置Grid的内边距
    }
    .backgroundColor(Color.Gray) // 设置自定义键盘的背景颜色为灰色
  }

  build() {
    Column() {
      // 创建一个TextInput组件，绑定controller和inputValue
      TextInput({ controller: this.controller, text: this.inputValue })
        .customKeyboard(this.CustomKeyboardBuilder()) // 设置自定义键盘为CustomKeyboardBuilder生成的键盘
        .margin(10) // 设置TextInput的外边距
        .border({ width: 1 }) // 设置TextInput的边框宽度
        .height('48vp') // 设置TextInput的高度
    }
  }
}

@Entry
@Component
struct TextInputExample {
  // 用于存储插入的文本内容
  @State insertValue: string = ""
  // 用于存储删除的文本内容
  @State deleteValue: string = ""
  // 用于存储插入的位置
  @State insertOffset: number = 0
  // 用于存储删除的位置
  @State deleteOffset: number = 0
  // 用于存储删除的方向，0表示向后删除，1表示向前删除
  @State deleteDirection: number = 0

  build() {
    Row() {
      Column() {
        // 第一个TextInput组件，支持插入回调
        TextInput({ text: "TextInput支持插入回调文本" })
          .height(60)
          // 插入前的回调函数，获取插入的文本内容
          .onWillInsert((info: InsertValue) => {
            this.insertValue = info.insertValue
            return true;
          })
          // 插入后的回调函数，获取插入的位置
          .onDidInsert((info: InsertValue) => {
            this.insertOffset = info.insertOffset
          })

        // 显示插入的文本内容和插入的位置
        Text("insertValue:" + this.insertValue + "  insertOffset:" + this.insertOffset).height(30)

        // 第二个TextInput组件，支持删除回调
        TextInput({ text: "TextInput支持删除回调文本b" })
          .height(60)
          // 删除前的回调函数，获取删除的文本内容和删除的方向
          .onWillDelete((info: DeleteValue) => {
            this.deleteValue = info.deleteValue
            info.direction
            return true;
          })
          // 删除后的回调函数，获取删除的位置和删除的方向
          .onDidDelete((info: DeleteValue) => {
            this.deleteOffset = info.deleteOffset
            this.deleteDirection = info.direction
          })

        // 显示删除的文本内容和删除的位置
        Text("deleteValue:" + this.deleteValue + "  deleteOffset:" + this.deleteOffset).height(30)
        // 显示删除的方向，0表示向后删除，1表示向前删除
        Text("deleteDirection:" + (this.deleteDirection == 0 ? "BACKWARD" : "FORWARD")).height(30)

      }.width('100%')
    }
    .height('100%')
  }
}

@Entry
@Component
struct TextInputExample {
  build() {
    Row() {
      Column() {
        // 显示标题，说明当前展示的是高度自适应策略
        Text('heightAdaptivePolicy').fontSize(9).fontColor(0xCCCCCC)

        // 第一个文本输入框，未设置高度自适应策略
        TextInput({text: 'This is the text without the height adaptive policy set'})
          .width('80%').height(50).borderWidth(1).margin(1)

        // 第二个文本输入框，设置高度自适应策略为MAX_LINES_FIRST
        TextInput({text: 'This is the text with the height adaptive policy set'})
          .width('80%').height(50).borderWidth(1).margin(1)
          .minFontSize(4) // 设置最小字体大小为4
          .maxFontSize(40) // 设置最大字体大小为40
          .maxLines(3) // 设置最大行数为3
          .heightAdaptivePolicy(TextHeightAdaptivePolicy.MAX_LINES_FIRST) // 高度自适应策略为优先适应最大行数

        // 第三个文本输入框，设置高度自适应策略为MIN_FONT_SIZE_FIRST
        TextInput({text: 'This is the text with the height adaptive policy set'})
          .width('80%').height(50).borderWidth(1).margin(1)
          .minFontSize(4) // 设置最小字体大小为4
          .maxFontSize(40) // 设置最大字体大小为40
          .maxLines(3) // 设置最大行数为3
          .heightAdaptivePolicy(TextHeightAdaptivePolicy.MIN_FONT_SIZE_FIRST) // 高度自适应策略为优先适应最小字体大小

        // 第四个文本输入框，设置高度自适应策略为LAYOUT_CONSTRAINT_FIRST
        TextInput({text: 'This is the text with the height adaptive policy set'})
          .width('80%').height(50).borderWidth(1).margin(1)
          .minFontSize(4) // 设置最小字体大小为4
          .maxFontSize(40) // 设置最大字体大小为40
          .maxLines(3) // 设置最大行数为3
          .heightAdaptivePolicy(TextHeightAdaptivePolicy.LAYOUT_CONSTRAINT_FIRST) // 高度自适应策略为优先适应布局约束
      }.height('90%')
    }
    .width('90%')
    .margin(10)
  }
}

@Entry
@Component
struct Input {
  // 创建一个TextInputController实例，用于控制TextInput组件的行为
  controller: TextInputController = new TextInputController()

  // 定义一个状态变量inputValue，用于存储输入框的文本内容
  @State inputValue: string = ""

  // 定义一个状态变量height1，用于控制输入框的高度，初始值为80%
  @State height1: string | number = '80%'

  // 定义一个状态变量supportAvoidance，用于控制是否支持键盘避让功能，初始值为true
  @State supportAvoidance: boolean = true;

  // 自定义键盘的构建器，用于生成自定义键盘的UI
  @Builder CustomKeyboardBuilder() {
    Column() {
      Row() {
        // 创建一个按钮，点击后停止编辑
        Button('x').onClick(() => {
          this.controller.stopEditing()
        }).margin(10)
      }
      Grid() {
        // 使用ForEach循环生成数字和符号按钮
        ForEach([1, 2, 3, 4, 5, 6, 7, 8, 9, '*', 0, '#'], (item: number | string) => {
          GridItem() {
            // 创建一个按钮，点击后将对应的数字或符号添加到输入框中
            Button(item + "").width(110).onClick(() => {
              this.inputValue += item
            })
          }
        })
      }.maxCount(3).columnsGap(10).rowsGap(10).padding(5)
    }.backgroundColor(Color.Gray)
  }

  build() {
    Column() {
      Row() {
        // 创建一个按钮，点击后将输入框高度设置为20%
        Button("20%").fontSize(24).onClick(() => {
          this.height1 = "20%"
        })
        // 创建一个按钮，点击后将输入框高度设置为80%
        Button("80%").fontSize(24).margin({ left: 20 }).onClick(() => {
          this.height1 = "80%"
        })
      }
      .justifyContent(FlexAlign.Center)
      .alignItems(VerticalAlign.Bottom)
      .height(this.height1) // 设置Row的高度为height1
      .width("100%")
      .padding({ bottom: 50 })

      // 创建一个TextInput组件，使用自定义键盘，并支持键盘避让功能
      TextInput({ controller: this.controller, text: this.inputValue })
        .customKeyboard(this.CustomKeyboardBuilder(), { supportAvoidance: this.supportAvoidance })
        .margin(10)
        .border({ width: 1 })
    }
  }
}

