@Entry
@Component
struct TextInputExample {
  // 定义密码输入框的图标资源
  @State PassWordSrc1: Resource = $r('app.media.onIcon') // 密码可见时的图标
  @State PassWordSrc2: Resource = $r('app.media.offIcon') // 密码不可见时的图标
  @State TextError: string = '' // 错误提示文本
  @State Text: string = '' // 用户输入的文本
  @State NameText: string = 'test' // 用户名验证的标准文本

  // 定义一个Builder方法，用于生成单位选择组件
  @Builder itemEnd() {
    Select([{ value: 'KB' },
      { value: 'MB' },
      { value: 'GB' },
      { value: 'TB', }])
      .height("48vp") // 设置高度
      .borderRadius(0) // 设置边框圆角
      .selected(2) // 设置默认选中项
      .align(Alignment.Center) // 设置对齐方式
      .value('MB') // 设置默认值
      .font({ size: 20, weight: 500 }) // 设置字体样式
      .fontColor('#182431') // 设置字体颜色
      .selectedOptionFont({ size: 20, weight: 400 }) // 设置选中项字体样式
      .optionFont({ size: 20, weight: 400 }) // 设置选项字体样式
      .backgroundColor(Color.Transparent) // 设置背景颜色
      .responseRegion({ height: "40vp", width: "80%", x: '10%', y: '6vp' }) // 设置响应区域
      .onSelect((index: number) => {
        console.info('Select:' + index) // 选择事件回调
      })
  }

  build() {
    Column({ space: 20 }) {

      // 第一个TextInput组件，展示自定义密码图标
      TextInput({ placeholder: 'user define password icon' })
        .type(InputType.Password) // 设置输入类型为密码
        .width(380) // 设置宽度
        .height(60) // 设置高度
        .passwordIcon({ onIconSrc: this.PassWordSrc1, offIconSrc: this.PassWordSrc2 }) // 设置密码图标

      // 第二个TextInput组件，展示下划线样式、错误提示和单位选择
      TextInput({ placeholder: 'underline style' })
        .showUnderline(true) // 显示下划线
        .width(380) // 设置宽度
        .height(60) // 设置高度
        .showError('Error') // 显示错误提示
        .showUnit(this.itemEnd) // 显示单位选择组件

      // 显示用户名输入的文本
      Text(`用户名：${this.Text}`)
        .width('95%') // 设置宽度

      // 第三个TextInput组件，用于输入用户名，并进行验证
      TextInput({ placeholder: '请输入用户名', text: this.Text })
        .showUnderline(true) // 显示下划线
        .width(380) // 设置宽度
        .showError(this.TextError) // 显示错误提示
        .onChange((value: string) => {
          this.Text = value // 输入内容变化时更新状态
        })
        .onSubmit((enterKey: EnterKeyType, event: SubmitEvent) => {
          if (this.Text == this.NameText) { // 验证用户名是否正确
            this.TextError = '' // 清除错误提示
          } else {
            this.TextError = '用户名输入错误' // 显示错误提示
            this.Text = '' // 清空输入内容
            event.keepEditableState() // 保持输入框的可编辑状态
          }
        })

    }.width('100%') // 设置Column的宽度
  }
}

@Entry
@Component
struct TextInputExample {
  @State text: string = 'TextInput editMenuOptions' // 初始化文本输入框的默认文本内容

  // 自定义编辑菜单的创建方法
  onCreateMenu(menuItems: Array<TextMenuItem>) {
    menuItems.forEach((value, index) => {
      value.icon = $r('app.media.startIcon') // 为每个菜单项设置相同的图标
      if (value.id.equals(TextMenuItemId.COPY)) {
        value.content = "复制change" // 修改“复制”菜单项的文本内容
      }
      if (value.id.equals(TextMenuItemId.SELECT_ALL)) {
        value.content = "全选change" // 修改“全选”菜单项的文本内容
      }
    })
    let item1: TextMenuItem = {
      content: 'custom1', // 自定义菜单项1的文本内容
      icon: $r('app.media.startIcon'), // 自定义菜单项1的图标
      id: TextMenuItemId.of('custom1'), // 自定义菜单项1的唯一标识符
    }
    let item2: TextMenuItem = {
      content: 'custom2', // 自定义菜单项2的文本内容
      id: TextMenuItemId.of('custom2'), // 自定义菜单项2的唯一标识符
      icon: $r('app.media.startIcon'), // 自定义菜单项2的图标
    }
    menuItems.push(item1) // 将自定义菜单项1添加到菜单项列表的末尾
    menuItems.unshift(item2) // 将自定义菜单项2添加到菜单项列表的开头
    return menuItems // 返回修改后的菜单项列表
  }

  build() {
    Column() {
      TextInput({ text: this.text }) // 创建一个文本输入组件，初始文本为@State变量text的值
        .width('95%') // 设置文本输入框的宽度为父容器的95%
        .height(50) // 设置文本输入框的高度为50像素
        .editMenuOptions({
          onCreateMenu: this.onCreateMenu, // 设置自定义编辑菜单的创建方法
          onMenuItemClick: (menuItem: TextMenuItem, textRange: TextRange) => { // 设置菜单项点击事件的回调方法
            if (menuItem.id.equals(TextMenuItemId.of("custom2"))) {
              console.log("拦截 id: custom2 start:" + textRange.start + "; end:" + textRange.end) // 拦截并打印自定义菜单项2的点击事件
              return true; // 返回true表示拦截该事件
            }
            if (menuItem.id.equals(TextMenuItemId.COPY)) {
              console.log("拦截 COPY start:" + textRange.start + "; end:" + textRange.end) // 拦截并打印“复制”菜单项的点击事件
              return true; // 返回true表示拦截该事件
            }
            if (menuItem.id.equals(TextMenuItemId.SELECT_ALL)) {
              console.log("不拦截 SELECT_ALL start:" + textRange.start + "; end:" + textRange.end) // 打印“全选”菜单项的点击事件，但不拦截
              return false; // 返回false表示不拦截该事件
            }
            return false; // 默认返回false，不拦截其他菜单项的点击事件
          }
        })
        .margin({ top: 100 }) // 设置文本输入框的上外边距为100像素
    }
    .width("90%") // 设置Column容器的宽度为父容器的90%
    .margin("5%") // 设置Column容器的左右外边距为父容器的5%
  }
}

@Entry
@Component
struct TextInputExample {
  // 定义一个状态变量text，用于存储用户输入的文本
  @State text: string = ''

  // 定义一个状态变量positionInfo，用于存储光标的位置信息
  @State positionInfo: CaretOffset = { index: 0, x: 0, y: 0 }

  // 定义一个状态变量passwordState，用于控制密码输入框的显示状态
  @State passwordState: boolean = false

  // 创建一个TextInputController实例，用于控制TextInput组件的行为
  controller: TextInputController = new TextInputController()

  build() {
    Column() {
      // 创建一个TextInput组件，用于输入普通文本
      TextInput({ text: this.text, placeholder: 'input your word...', controller: this.controller })
        .placeholderColor(Color.Grey) // 设置占位符文本的颜色为灰色
        .placeholderFont({ size: 14, weight: 400 }) // 设置占位符文本的字体大小和粗细
        .caretColor(Color.Blue) // 设置光标的颜色为蓝色
        .width('95%') // 设置组件的宽度为父容器的95%
        .height(40) // 设置组件的高度为40
        .margin(20) // 设置组件的外边距为20
        .fontSize(14) // 设置输入文本的字体大小为14
        .fontColor(Color.Black) // 设置输入文本的颜色为黑色
        .inputFilter('\[a-z\]', (e) => { // 设置输入过滤器，只允许输入小写字母
          console.log(JSON.stringify(e)) // 打印过滤事件的详细信息
        })
        .onChange((value: string) => { // 监听输入文本的变化事件
          this.text = value // 更新状态变量text的值
        })

