import worker from '@ohos.worker';

// 定义一个组件，作为页面的入口
@Entry
@Component
struct imageBitmapExamplePage {
  // 创建一个RenderingContextSettings对象，用于配置Canvas的渲染上下文
  private settings: RenderingContextSettings = new RenderingContextSettings(true);

  // 创建一个CanvasRenderingContext2D对象，用于在Canvas上进行绘图操作
  private context: CanvasRenderingContext2D = new CanvasRenderingContext2D(this.settings);

  // 创建一个Worker线程，用于处理图像数据
  private myWorker = new worker.ThreadWorker('entry/ets/workers/Worker.ts');

  // 创建一个ImageBitmap对象，用于加载图像数据
  private img: ImageBitmap = new ImageBitmap("common/images/example.jpg");

  // 构建页面的UI结构
  build() {
    // 使用Flex布局，设置布局方向为列，居中对齐
    Flex({ direction: FlexDirection.Column, alignItems: ItemAlign.Center, justifyContent: FlexAlign.Center }) {
      // 创建一个Canvas组件，并绑定渲染上下文
      Canvas(this.context)
        .width('100%') // 设置Canvas宽度为100%
        .height('100%') // 设置Canvas高度为100%
        .backgroundColor('#ffff00') // 设置Canvas背景颜色为黄色
        .onReady(() => {
          // 当Canvas准备好后，向Worker线程发送图像数据
          this.myWorker.postMessage({ myImage: this.img });

          // 监听Worker线程的消息
          this.myWorker.onmessage = (e): void => {
            // 如果接收到Worker线程返回的图像数据
            if (e.data.myImage) {
              // 获取处理后的图像数据
              let image: ImageBitmap = e.data.myImage;

              // 将处理后的图像数据绘制到Canvas上
              this.context.transferFromImageBitmap(image);
            }
          }
        })
    }
    .width('100%') // 设置Flex容器的宽度为100%
    .height('100%') // 设置Flex容器的高度为100%
  }
}

// Worker线程的逻辑
workerPort.onmessage = function (e: MessageEvents) {
  // 如果接收到主线程发送的图像数据
  if (e.data.myImage) {
    // 获取图像数据
    let img = e.data.myImage;

    // 创建一个OffscreenCanvas对象，用于在Worker线程中进行图像处理
    let offCanvas = new OffscreenCanvas(600, 600);

    // 获取OffscreenCanvas的2D渲染上下文
    let offContext = offCanvas.getContext("2d");

    // 在OffscreenCanvas上绘制图像，并进行缩放和裁剪
    offContext.drawImage(img, 0, 0, 500, 500, 0, 0, 400, 200);

    // 将OffscreenCanvas的内容转换为ImageBitmap对象
    let image = offCanvas.transferToImageBitmap();

    // 将处理后的图像数据发送回主线程
    workerPort.postMessage({ myImage: image });
  }
}

