import { EmbeddedUIExtensionAbility, UIExtensionContentSession, Want } from '@kit.AbilityKit';

const TAG: string = '[ExampleEmbeddedAbility]'

export default class ExampleEmbeddedAbility extends EmbeddedUIExtensionAbility {

  // 生命周期方法：在能力创建时触发，用于初始化资源或状态
  onCreate() {
    console.log(TAG, `onCreate`);
  }

  // 生命周期方法：当能力从后台切换到前台时触发，通常用于恢复UI状态或刷新数据
  onForeground() {
    console.log(TAG, `onForeground`);
  }

  // 生命周期方法：当能力切换到后台时触发，用于保存状态或释放资源
  onBackground() {
    console.log(TAG, `onBackground`);
  }

  // 生命周期方法：在能力销毁时触发，进行清理工作
  onDestroy() {
    console.log(TAG, `onDestroy`);
  }

  // 会话管理方法：当嵌入式UI扩展会话创建时触发，接收 Want 对象和 UIExtensionContentSession 会话对象
  onSessionCreate(want: Want, session: UIExtensionContentSession) {
    console.log(TAG, `onSessionCreate, want: ${JSON.stringify(want)}`);
    let param: Record<string, UIExtensionContentSession> = {
      'session': session
    };
    let storage: LocalStorage = new LocalStorage(param);
    // 加载指定路径的内容页面，并传递自定义存储对象
    session.loadContent('pages/extension', storage);
  }

  // 会话管理方法：当会话销毁时触发，用于清理会话相关的资源
  onSessionDestroy(session: UIExtensionContentSession) {
    console.log(TAG, `onSessionDestroy`);
  }
}

