@Component
struct FlexExample3 {
  build() {
    // 创建一个Column容器，用于垂直排列子组件
    Column() {
      // 创建一个Column容器，设置子组件之间的间距为5
      Column({ space: 5 }) {
        // 创建一个Text组件，显示'justifyContent:Start'，设置字体大小为9，字体颜色为0xCCCCCC，宽度为90%
        Text('justifyContent:Start').fontSize(9).fontColor(0xCCCCCC).width('90%')
        // 创建一个JustifyContentFlex组件，设置justifyContent属性为FlexAlign.Start，子组件在容器主轴上首端对齐
        JustifyContentFlex({ justifyContent: FlexAlign.Start })

        // 创建一个Text组件，显示'justifyContent:Center'，设置字体大小为9，字体颜色为0xCCCCCC，宽度为90%
        Text('justifyContent:Center').fontSize(9).fontColor(0xCCCCCC).width('90%')
        // 创建一个JustifyContentFlex组件，设置justifyContent属性为FlexAlign.Center，子组件在容器主轴上居中对齐
        JustifyContentFlex({ justifyContent: FlexAlign.Center })

        // 创建一个Text组件，显示'justifyContent:End'，设置字体大小为9，字体颜色为0xCCCCCC，宽度为90%
        Text('justifyContent:End').fontSize(9).fontColor(0xCCCCCC).width('90%')
        // 创建一个JustifyContentFlex组件，设置justifyContent属性为FlexAlign.End，子组件在容器主轴上尾端对齐
        JustifyContentFlex({ justifyContent: FlexAlign.End })

        // 创建一个Text组件，显示'justifyContent:SpaceBetween'，设置字体大小为9，字体颜色为0xCCCCCC，宽度为90%
        Text('justifyContent:SpaceBetween').fontSize(9).fontColor(0xCCCCCC).width('90%')
        // 创建一个JustifyContentFlex组件，设置justifyContent属性为FlexAlign.SpaceBetween，子组件在容器主轴上均分容器布局，第一个子组件与行首对齐，最后一个子组件与行尾对齐
        JustifyContentFlex({ justifyContent: FlexAlign.SpaceBetween })

        // 创建一个Text组件，显示'justifyContent:SpaceAround'，设置字体大小为9，字体颜色为0xCCCCCC，宽度为90%
        Text('justifyContent:SpaceAround').fontSize(9).fontColor(0xCCCCCC).width('90%')
        // 创建一个JustifyContentFlex组件，设置justifyContent属性为FlexAlign.SpaceAround，子组件在容器主轴上均分容器布局，第一个子组件到行首的距离和最后一个子组件到行尾的距离是相邻子组件之间距离的一半
        JustifyContentFlex({ justifyContent: FlexAlign.SpaceAround })

        // 创建一个Text组件，显示'justifyContent:SpaceEvenly'，设置字体大小为9，字体颜色为0xCCCCCC，宽度为90%
        Text('justifyContent:SpaceEvenly').fontSize(9).fontColor(0xCCCCCC).width('90%')
        // 创建一个JustifyContentFlex组件，设置justifyContent属性为FlexAlign.SpaceEvenly，子组件在容器主轴上均分容器布局，子组件之间的距离与第一子组件到行首、最后一个子组件到行尾的距离相等
        JustifyContentFlex({ justifyContent: FlexAlign.SpaceEvenly })
      }
      // 设置Column容器的宽度为100%，并设置顶部外边距为5
      .width('100%').margin({ top: 5 })
    }
    // 设置Column容器的宽度为100%
    .width('100%')
  }
}

