@Entry
@Component
struct GridRowExample {
  // 定义一个状态变量 bgColors，存储颜色数组，用于网格列的背景色
  @State bgColors: Color[] = [Color.Red, Color.Orange, Color.Yellow, Color.Green, Color.Pink, Color.Grey, Color.Blue, Color.Brown]
  // 定义一个状态变量 currentBp，用于存储当前的断点状态
  @State currentBp: string = 'unknown'

  build() {
    Column() {
      // 创建一个 GridRow 组件，设置列数、间距、断点等属性
      GridRow({
        columns: 5, // 默认列数为5
        gutter: { x: 5, y: 10 }, // 列之间的水平间距为5，垂直间距为10
        breakpoints: { 
          value: ["400vp", "600vp", "800vp"], // 定义断点值，分别为400vp、600vp、800vp
          reference: BreakpointsReference.WindowSize // 断点参考值为窗口大小
        },
        direction: GridRowDirection.Row // 网格方向为行
      }) {
        // 使用 ForEach 循环遍历 bgColors 数组，为每个颜色创建一个 GridCol 组件
        ForEach(this.bgColors, (color: Color) => {
          GridCol({ 
            span: { xs: 1, sm: 2, md: 3, lg: 4 }, // 根据不同的屏幕尺寸设置列的跨度
            offset: 0, // 列的偏移量为0
            order: 0 // 列的顺序为0
          }) {
            // 创建一个 Row 组件，设置宽度和高度
            Row().width("100%").height("20vp")
          }.borderColor(color).borderWidth(2) // 设置列的边框颜色和宽度
        })
      }.width("100%").height("100%") // 设置 GridRow 的宽度和高度为100%
      .onBreakpointChange((breakpoint) => {
        // 监听断点变化事件，更新 currentBp 状态变量
        this.currentBp = breakpoint
      })
    }.width('80%').margin({ left: 10, top: 5, bottom: 5 }).height(200) // 设置 Column 的宽度、外边距和高度
    .border({ color: '#880606', width: 2 }) // 设置 Column 的边框颜色和宽度
  }
}

@Entry
@Component
struct GridExample {
  @State numbers: string[] = [] // 定义一个状态变量numbers，用于存储展示的数字字符串数组

  // 组件初始化时填充数据
  aboutToAppear() {
    for (let i = 1; i <= 30; i++) {
      this.numbers.push(i + '') // 将1到30的数字转换为字符串并添加到numbers数组中
    }
  }

  build() {
    Scroll() { // 创建一个可滚动的容器
      Column({ space: 5 }) { // 创建一个垂直布局容器，设置子组件之间的间距为5
        Blank() // 插入一个空白组件，用于占位
        Text('rowsTemplate、columnsTemplate都不设置layoutDirection、maxcount、minCount、cellLength才生效')
          .fontSize(15).fontColor(0xCCCCCC).width('90%') // 创建一个文本组件，设置字体大小、颜色和宽度

        Grid() { // 创建一个网格布局容器
          ForEach(this.numbers, (day: string) => { // 遍历numbers数组，为每个数字创建一个网格项
            GridItem() { // 创建一个网格项
              Text(day).fontSize(16).backgroundColor(0xF9CF93) // 创建一个文本组件，设置字体大小和背景颜色
            }.width(40).height(80).borderWidth(2).borderColor(Color.Red) // 设置网格项的宽度、高度、边框宽度和颜色
          }, (day: string) => day) // 指定key生成函数，确保每个网格项的唯一性
