      this.numbers.push(i + '')
    }
  }

  // 交换数组中两个元素的位置
  changeIndex(index1: number, index2: number) { 
    let temp: string;
    temp = this.numbers[index1];
    this.numbers[index1] = this.numbers[index2];
    this.numbers[index2] = temp;
  }

  // 构建Grid组件
  build() {
