                      .height('100%') // 设置高度为100%
                      .textAlign(TextAlign.Center) // 设置文本居中对齐
                  }
                  .width('100%')
                  .height(40)
                  .shadow({ radius: 10, color: '#909399', offsetX: 1, offsetY: 1 }) // 设置阴影效果
                  .borderRadius(10) // 设置圆角
                  .translate({ x: 0, y: this.translateY }) // 设置平移效果
                }, (item: string) => item)
              }
              .columnsTemplate('1fr 1fr') // 设置列模板
              .friction(0.3) // 设置摩擦系数
              .columnsGap(15) // 设置列间距
              .rowsGap(10) // 设置行间距
              .scrollBar(BarState.Off) // 关闭滚动条
              .width('100%') // 设置宽度为100%
              .height('100%') // 设置高度为100%
              .layoutDirection(GridDirection.Column) // 设置布局方向为列
              .nestedScroll({
                scrollForward: NestedScrollMode.PARENT_FIRST, // 设置向前滚动时优先滚动父组件
                scrollBackward: NestedScrollMode.SELF_FIRST // 设置向后滚动时优先滚动自身
              })
              .onTouch((event: TouchEvent) => {
                if (event.type == TouchType.Down) {
                  this.listTouchDown = true // 记录触摸事件为按下状态
                } else if (event.type == TouchType.Up) {
                  this.listTouchDown = false // 记录触摸事件为抬起状态
                }
              })
            }
          }
          .scrollBar(BarState.Off) // 关闭滚动条
          .edgeEffect(EdgeEffect.None) // 关闭边缘效果
          .onTouch((event: TouchEvent) => {
            if (event.type == TouchType.Down) {
              this.touchDown = true // 记录触摸事件为按下状态
            } else if (event.type == TouchType.Up) {
              this.touchDown = false // 记录触摸事件为抬起状态
            }
          })
          .onScrollFrameBegin((offset: number, state: ScrollState) => {
            if (this.scrolling && offset > 0) {
              let newOffset = this.scroller.currentOffset().yOffset
              if (newOffset >= 590) {
                this.gridScroller.scrollBy(0, offset) // 滚动Grid组件
                return { offsetRemain: 0 } // 返回剩余偏移量为0
              } else if (newOffset + offset > 590) {
                this.gridScroller.scrollBy(0, newOffset + offset - 590)
                return { offsetRemain: 590 - newOffset } // 返回剩余偏移量
              }
            }
            return { offsetRemain: offset } // 返回剩余偏移量
          })
          .onScrollStart(() => {
            if (this.touchDown && !this.listTouchDown) {
              this.scrolling = true // 记录正在滚动
            }
          })
          .onScrollStop(() => {
            this.scrolling = false // 记录滚动停止
          })
        }
        .width('100%') // 设置宽度为100%
        .height('100%') // 设置高度为100%
        .padding({ left: 10, right: 10 }) // 设置左右内边距
      }

      Row() {
        Text('Top')
          .width(30) // 设置宽度为30
          .height(30) // 设置高度为30
          .borderRadius(50) // 设置圆角
      }
      .padding(5) // 设置内边距
      .borderRadius(50) // 设置圆角
      .backgroundColor('#ffffff') // 设置背景颜色
      .shadow({ radius: 10, color: '#909399', offsetX: 1, offsetY: 1 }) // 设置阴影效果
      .margin({ right: 22, bottom: 15 }) // 设置外边距
      .onClick(() => {
        this.scroller.scrollTo({ xOffset: 0, yOffset: 0 }) // 滚动到顶部
        this.gridScroller.scrollTo({ xOffset: 0, yOffset: 0 }) // 滚动到顶部
      })
    }
    .align(Alignment.BottomEnd) // 设置对齐方式为右下角
  }
}

