@Component
struct ListExample {
  // 定义一个数组，用于存储列表项的数据
  private arr: number[] = []

  // 创建一个ListScroller对象，用于控制列表的滚动
  private scroller: ListScroller = new ListScroller()

  // 定义列表项之间的间距，初始值为10
  @State listSpace: number = 10

  // 定义列表子组件的主轴尺寸，初始值为100
  @State listChildrenSize: ChildrenMainSize = new ChildrenMainSize(100)

  // 组件即将显示时执行的初始化操作
  aboutToAppear(){
    // 向数组中添加10个数字
    for (let i = 0; i < 10; i++) {
      this.arr.push(i)
    }

    // 设置前5个列表项的高度为300
    this.listChildrenSize.splice(0, 5, [300, 300, 300, 300, 300])
  }

  build() {
    Column() {
      // 创建一个List组件，设置间距、初始索引、滚动控制器
      List({ space: this.listSpace, initialIndex: 4, scroller: this.scroller }) {
        // 使用ForEach循环遍历数组，生成列表项
        ForEach(this.arr, (item: number) => {
          ListItem() {
            // 创建一个Text组件，显示列表项的内容
            Text('item-' + item)
              .height( item < 5 ? 300 : this.listChildrenSize.childDefaultSize) // 根据条件设置高度
              .width('90%') // 设置宽度为90%
              .fontSize(16) // 设置字体大小为16
              .textAlign(TextAlign.Center) // 设置文本居中对齐
              .borderRadius(10) // 设置边框圆角为10
              .backgroundColor(0xFFFFFF) // 设置背景颜色为白色
          }
        }, (item: string) => item) // 指定键值生成函数
      }
      .backgroundColor(Color.Gray) // 设置列表背景颜色为灰色
      .layoutWeight(1) // 设置布局权重为1
      .scrollBar(BarState.On) // 显示滚动条
      .childrenMainSize(this.listChildrenSize) // 设置子组件的主轴尺寸
      .alignListItem(ListItemAlign.Center) // 设置列表项居中对齐

      // 创建一个Row组件，包含三个按钮
      Row(){
        // 第一个按钮，点击后增加列表项的高度
        Button() { Text('item size + 50') }.onClick(()=>{
          this.listChildrenSize.childDefaultSize += 50
        }).height('50%').width('30%')

        // 第二个按钮，点击后减少列表项的高度
        Button() { Text('item size - 50') }.onClick(()=>{
          if (this.listChildrenSize.childDefaultSize === 0) {
            return
          }
          this.listChildrenSize.childDefaultSize -= 50
        }).height('50%').width('30%')

        // 第三个按钮，点击后调用scrollTo方法跳转到指定位置
        Button() { Text('scrollTo (0, 310)') }.onClick(()=>{
          this.scroller.scrollTo({xOffset: 0, yOffset: 310})
        }).height('50%').width('30%')
      }.height('20%') // 设置Row的高度为20%
    }
  }
}

