  @State enterEndDeleteAreaString: string = "not enterEndDeleteArea"

  // 定义一个状态变量 exitEndDeleteAreaString，用于存储滑动退出删除区域时的提示信息
  @State exitEndDeleteAreaString: string = "not exitEndDeleteArea"

  // 定义一个 Builder 方法 itemEnd，用于生成列表项右侧的删除和设置按钮
  @Builder itemEnd() {
    Row() {
      Button("Delete").margin("4vp") // 创建一个删除按钮，并设置边距
      Button("Set").margin("4vp") // 创建一个设置按钮，并设置边距
    }.padding("4vp").justifyContent(FlexAlign.SpaceEvenly) // 设置按钮容器的内边距和对齐方式
  }

  // 定义组件的 build 方法，用于构建 UI 结构
  build() {
    Column() {
      List({ space: 10 }) { // 创建一个列表组件，设置列表项之间的间距为 10
        ForEach(this.arr, (item: number) => { // 遍历 arr 数组，生成列表项
          ListItem() {
            Text("item" + item) // 创建一个文本组件，显示列表项的内容
              .width('100%') // 设置文本组件的宽度为 100%
              .height(100) // 设置文本组件的高度为 100
              .fontSize(16) // 设置文本的字体大小为 16
              .textAlign(TextAlign.Center) // 设置文本居中对齐
              .borderRadius(10) // 设置文本组件的圆角半径为 10
              .backgroundColor(0xFFFFFF) // 设置文本组件的背景颜色为白色
          }
          .transition({ type: TransitionType.Delete, opacity: 0 }) // 设置列表项删除时的过渡动画，透明度从 1 变为 0
          .swipeAction({
            end: {
