    .backgroundColor('#DCDCDC') // 设置背景颜色为灰色
    .scrollBar(BarState.Off) // 关闭滚动条
    .width('100%') // 设置Scroll的宽度为100%
    .height('100%') // 设置Scroll的高度为100%
  }

  aboutToAppear() { // 组件即将显示时的生命周期方法
