@Entry
@Component
struct SwiperCustomAnimationExample {
  // 定义常量，表示同时显示的页面数量
  private DISPLAY_COUNT: number = 2
  // 定义常量，表示页面缩放的最小比例
  private MIN_SCALE: number = 0.75

  // 定义状态变量，存储每个页面的背景颜色
  @State backgroundColors: Color[] = [Color.Green, Color.Blue, Color.Yellow, Color.Pink, Color.Gray, Color.Orange]
  // 定义状态变量，存储每个页面的透明度
  @State opacityList: number[] = []
  // 定义状态变量，存储每个页面的缩放比例
  @State scaleList: number[] = []
  // 定义状态变量，存储每个页面的平移距离
  @State translateList: number[] = []
  // 定义状态变量，存储每个页面的层级
  @State zIndexList: number[] = []

  // 组件即将显示时调用的方法，初始化每个页面的透明度、缩放比例、平移距离和层级
  aboutToAppear(): void {
    for (let i = 0; i < this.backgroundColors.length; i++) {
      this.opacityList.push(1.0)
      this.scaleList.push(1.0)
      this.translateList.push(0.0)
      this.zIndexList.push(0)
    }
  }

  // 构建Swiper组件
  build() {
    Column() {
      Swiper() {
        // 使用ForEach循环遍历背景颜色数组，生成每个页面
        ForEach(this.backgroundColors, (backgroundColor: Color, index: number) => {
          Text(index.toString()).width('100%').height('100%').fontSize(50).textAlign(TextAlign.Center)
            .backgroundColor(backgroundColor)
            // 设置页面的透明度
            .opacity(this.opacityList[index])
            // 设置页面的缩放比例
            .scale({ x: this.scaleList[index], y: this.scaleList[index] })
            // 设置页面的平移距离
            .translate({ x: this.translateList[index] })
            // 设置页面的层级
            .zIndex(this.zIndexList[index])
        })
      }
      .height(300)
      // 隐藏Swiper的指示器
      .indicator(false)
      // 设置同时显示的页面数量
      .displayCount(this.DISPLAY_COUNT, true)
      // 自定义页面切换动画
      .customContentTransition({
        timeout: 1000,
        transition: (proxy: SwiperContentTransitionProxy) => {
          // 根据页面的位置和索引，动态调整页面的透明度、缩放比例、平移距离和层级
          if (proxy.position <= proxy.index % this.DISPLAY_COUNT || proxy.position >= this.DISPLAY_COUNT + proxy.index % this.DISPLAY_COUNT) {
            this.opacityList[proxy.index] = 1.0
            this.scaleList[proxy.index] = 1.0
            this.translateList[proxy.index] = 0.0
            this.zIndexList[proxy.index] = 0
          } else {
            if (proxy.index % this.DISPLAY_COUNT === 0) {
              this.opacityList[proxy.index] = 1 - proxy.position / this.DISPLAY_COUNT
              this.scaleList[proxy.index] = this.MIN_SCALE + (1 - this.MIN_SCALE) * (1 - proxy.position / this.DISPLAY_COUNT)
              this.translateList[proxy.index] = - proxy.position * proxy.mainAxisLength + (1 - this.scaleList[proxy.index]) * proxy.mainAxisLength / 2.0
            } else {
              this.opacityList[proxy.index] = 1 - (proxy.position - 1) / this.DISPLAY_COUNT
              this.scaleList[proxy.index] = this.MIN_SCALE + (1 - this.MIN_SCALE) * (1 - (proxy.position - 1) / this.DISPLAY_COUNT)
              this.translateList[proxy.index] = - (proxy.position - 1) * proxy.mainAxisLength - (1 - this.scaleList[proxy.index]) * proxy.mainAxisLength / 2.0
            }
            this.zIndexList[proxy.index] = -1
          }
        }
      })
      // 监听Swiper内容滚动事件，打印当前选中的索引、页面的索引、位置和主轴长度
      .onContentDidScroll((selectedIndex: number, index: number, position: number, mainAxisLength: number) => {
        console.info("onContentDidScroll selectedIndex: " + selectedIndex + ", index: " + index + ", position: " + position + ", mainAxisLength: " + mainAxisLength)
      })
    }.width('100%')
  }
}

