@Entry
@Component
struct SwiperExample {
  // 轮播图控制器
  private swiperController: SwiperController = new SwiperController()
  // 数据源实例
  private data: MyDataSource = new MyDataSource([])

  // 组件即将显示时执行的逻辑
  aboutToAppear(): void {
    let list: number[] = []
    // 生成1到10的数字列表
    for (let i = 1; i <= 10; i++) {
      list.push(i);
    }
    // 初始化数据源
    this.data = new MyDataSource(list)
  }

  // 构建UI结构
  build() {
    Column({ space: 5 }) {
      // 轮播图组件
      Swiper(this.swiperController) {
        // 使用LazyForEach遍历数据源并生成Text组件
        LazyForEach(this.data, (item: string) => {
          Text(item.toString())
            .width('90%')
            .height(160)
            .backgroundColor(0xAFEEEE)
            .textAlign(TextAlign.Center)
            .fontSize(30)
        }, (item: string) => item)
      }
      .cachedCount(2) // 设置缓存项数
      .index(1) // 设置初始显示的索引
      .autoPlay(true) // 开启自动播放
      .interval(4000) // 设置自动播放间隔时间
      .loop(true) // 开启循环播放
      .indicatorInteractive(true) // 开启导航点交互
      .duration(1000) // 设置动画持续时间
      .itemSpace(0) // 设置项间距
      .indicator( 
        new DotIndicator()
          .itemWidth(15) // 设置导航点宽度
          .itemHeight(15) // 设置导航点高度
